// src/components/HomeProductCard.js
import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { toggleWishlist } from "../actions/wishlistActions";
import "./HomeProducts.css";

const HomeProductCard = ({ product }) => {
  const dispatch = useDispatch();
  const { items: wishlistItems = [] } = useSelector((s) => s.wishlist || {});
  const inWishlist = wishlistItems.some((w) => (w.id || w._id) === product.id);

  const handleWishlist = () => {
    dispatch(toggleWishlist(product.id));
  };

  return (
    <div className="hp-card">
      {/* Stock badge */}
      {!product.stock && <span className="hp-badge">Out of stock</span>}

      {/* Product image */}
      <div className="hp-image">
        <img src={product.image} alt={product.name} />
      </div>

      {/* Action icons overlay */}
      <div className="hp-actions">
        {/* View product */}
        <Link to={`/product/${product.id}`} className="hp-icon" title="View details">
          <i className="fas fa-eye"></i>
        </Link>

        {/* Toggle wishlist */}
        <button
          onClick={handleWishlist}
          className={`hp-icon ${inWishlist ? "active" : ""}`}
          title={inWishlist ? "Remove from wishlist" : "Add to wishlist"}
        >
          <i className={inWishlist ? "fas fa-heart" : "far fa-heart"}></i>
        </button>
      </div>

      {/* Product info */}
      <div className="hp-info">
        <h6 className="hp-title">{product.name}</h6>
        <div className="hp-price">
          {new Intl.NumberFormat("fr-MA", {
            style: "currency",
            currency: "MAD",
          }).format(Number(product.price || 0))}
        </div>
      </div>
    </div>
  );
};

export default HomeProductCard;
