// src/pages/ShippingRatesPage.js
import React, { useEffect, useMemo, useState } from "react";
import "./shipping-rates.css";

// Si tu as une API, mets l'URL ici (sinon laisse vide pour utiliser les données locales)
const API_URL = ""; // ex: "/api/shipping-rates"

const FALLBACK_RATES = [
  // === EXEMPLES (à remplacer par ta vraie liste) ===
  { city: "Agadir", price: 35 },
  { city: "AFRA-nador", price: 45 },
  { city: "Afourar - Beni-Mellal", price: 45 },
  { city: "aazayeb-chefchaouen", price: 45 },
  { city: "Nowasser-chichaoua", price: 45 },
  { city: "MEHDIA", price: 45 },
  { city: "Lalla Fatna", price: 45 },
  { city: "Kasbah El Taher", price: 45 },
  { city: "jamaat fdala", price: 40 },
  { city: "ighram laalam-beni mellal", price: 45 },
  // Ajoute toutes tes villes ici…
];

export default function ShippingRatesPage() {
  const [rows, setRows] = useState(FALLBACK_RATES);
  const [loading, setLoading] = useState(!!API_URL);
  const [error, setError] = useState(null);

  // Toolbar state
  const [pageSize, setPageSize] = useState(10);
  const [q, setQ] = useState("");
  const [page, setPage] = useState(1);

  // Charger depuis l’API si tu renseignes API_URL
  useEffect(() => {
    let active = true;
    if (!API_URL) return;
    (async () => {
      try {
        const res = await fetch(API_URL);
        if (!res.ok) throw new Error("Erreur de chargement");
        const data = await res.json();
        if (!active) return;

        // Normalise: on accepte {city, price} ou {ville, tarif}
        const normalized = (Array.isArray(data) ? data : []).map((r) => ({
          city: r.city || r.ville || "",
          price: Number(r.price ?? r.tarif ?? 0),
        }));
        setRows(normalized);
        setLoading(false);
      } catch (e) {
        if (active) {
          setError(e.message || "Erreur");
          setLoading(false);
        }
      }
    })();
    return () => {
      active = false;
    };
  }, []);

  // Filtrage
  const filtered = useMemo(() => {
    const needle = q.trim().toLowerCase();
    if (!needle) return rows;
    return rows.filter((r) => r.city.toLowerCase().includes(needle));
  }, [q, rows]);

  // Pagination
  const totalPages = Math.max(1, Math.ceil(filtered.length / pageSize));
  const currentPage = Math.min(page, totalPages);
  const start = (currentPage - 1) * pageSize;
  const pageRows = filtered.slice(start, start + pageSize);

  const goto = (p) => setPage(Math.min(Math.max(1, p), totalPages));

  return (
    <div className="sr-wrap">
      {/* (optionnel) logo */}
      <div className="sr-brand">
        {/* Mets ton logo si tu veux */}
        {/* <img src="/logo.png" alt="logo" /> */}
      </div>

      <h1 className="sr-title">Tarifs de Livraison</h1>

      {/* Toolbar */}
      <div className="sr-toolbar">
        <label className="sr-rows">
          Afficher{" "}
          <select
            value={pageSize}
            onChange={(e) => {
              setPageSize(Number(e.target.value));
              setPage(1);
            }}
          >
            {[10, 25, 50, 100].map((n) => (
              <option key={n} value={n}>
                {n}
              </option>
            ))}
          </select>{" "}
          entrées
        </label>

        <label className="sr-search">
          Rechercher :{" "}
          <input
            type="search"
            value={q}
            onChange={(e) => {
              setQ(e.target.value);
              setPage(1);
            }}
            placeholder="Ville…"
          />
        </label>
      </div>

      {/* Table */}
      <div className="sr-card">
        {loading ? (
          <div className="sr-empty">Chargement…</div>
        ) : error ? (
          <div className="sr-error">Erreur : {error}</div>
        ) : (
          <div className="sr-table-wrap">
            <table className="sr-table">
              <thead>
                <tr>
                  <th className="sr-th-city">Ville</th>
                  <th>Tarif Livraison (DH)</th>
                </tr>
              </thead>
              <tbody>
                {pageRows.length === 0 ? (
                  <tr>
                    <td colSpan={2} className="sr-empty">
                      Aucune entrée.
                    </td>
                  </tr>
                ) : (
                  pageRows.map((r, i) => (
                    <tr key={`${r.city}-${i}`}>
                      <td>{r.city}</td>
                      <td>{Number(r.price).toFixed(0)}</td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Footer/pagination */}
      {!loading && !error && (
        <div className="sr-footer">
          <div className="sr-count">
            Affichage de {start + 1} à {Math.min(start + pageSize, filtered.length)} sur{" "}
            {filtered.length} entrées
          </div>

          <div className="sr-pager">
            <button onClick={() => goto(currentPage - 1)} disabled={currentPage === 1}>
              Précédente
            </button>
            {Array.from({ length: totalPages }).slice(0, 7).map((_, idx) => {
              // Petite pagination compacte (1 2 3 … N)
              if (totalPages > 7 && idx === 5)
                return (
                  <span key="dots" className="sr-dots">
                    …
                  </span>
                );
              const p = totalPages > 7 && idx >= 6 ? totalPages : idx + 1;
              return (
                <button
                  key={p}
                  onClick={() => goto(p)}
                  className={p === currentPage ? "is-active" : ""}
                >
                  {p}
                </button>
              );
            })}
            <button
              onClick={() => goto(currentPage + 1)}
              disabled={currentPage === totalPages}
            >
              Suivante
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
