// src/components/Navbar.js
import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Navbar, Nav, Container } from 'react-bootstrap'
import { LinkContainer } from 'react-router-bootstrap'
import { logout } from '../actions/userActions'
import { useHistory } from "react-router-dom"
import SearchBarForProducts from './SearchBarForProducts'

function NavBar() {
  let history = useHistory()
  const dispatch = useDispatch()

  // login reducer
  const userLoginReducer = useSelector(state => state.userLoginReducer)
  const { userInfo } = userLoginReducer

  // logout
  const logoutHandler = () => {
    dispatch(logout())
    history.push("/login")
    window.location.reload()
  }

  return (
    <header className="site-header">
      {/* TOP BAR with logo + icons */}
      <div className="brandbar">
        <Container className="brandbar-inner">
          <div className="brandbar-left"></div>

          {/* Logo */}
          <div className="brandbar-logo">
            <LinkContainer to="/">
              <img src="/logo.png" alt="Logo" className="brand-logo-img" />
            </LinkContainer>
          </div>

          {/* Icons */}
          <div className="brandbar-icons">
            {/* Search bar on top-right */}
            <SearchBarForProducts />

            {/* Wishlist */}
            <LinkContainer to="/wishlist">
              <a className="icon-link"><i className="fas fa-heart"></i></a>
            </LinkContainer>

            {/* Cart */}
            <LinkContainer to="/cart">
              <a className="icon-link"><i className="fas fa-shopping-cart"></i></a>
            </LinkContainer>
          </div>
        </Container>
      </div>

      {/* NAV STRIP */}
      <Navbar className="navstrip" expand="lg" collapseOnSelect>
        <Container>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <LinkContainer to="/products">
                <Nav.Link className="nav-link-upp">All Products</Nav.Link>
              </LinkContainer>

              {/* New Product visible uniquement admin */}
              {userInfo && userInfo.admin && (
                <LinkContainer to="/new-product/">
                  <Nav.Link className="nav-link-upp">Add Product</Nav.Link>
                </LinkContainer>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  )
}

export default NavBar
