import React, { useState } from "react";
import axios from "axios";
import "./contact.css";

export default function ContactPage() {
  const [name, setName]   = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [message, setMessage] = useState("");

  const [sending, setSending] = useState(false);
  const [ok, setOk] = useState("");
  const [err, setErr] = useState("");

  const submit = async (e) => {
    e.preventDefault();
    setErr(""); setOk("");
    if (!name || !email || !message) { setErr("Veuillez remplir les champs obligatoires."); return; }
    setSending(true);
    try {
      await axios.post("/api/contact/", { name, email, phone, message });
      setOk("Merci ! Votre message a bien été envoyé.");
      setName(""); setEmail(""); setPhone(""); setMessage("");
    } catch (e) {
      setErr(e?.response?.data?.detail || "Erreur lors de l’envoi.");
    } finally {
      setSending(false);
    }
  };

  return (
    <div className="c-wrap">
      <h1 className="c-title">Contacter nous</h1>

      <div className="c-info">
        <div><b>Adresse :</b> Casablanca, Maroc</div>
        <div><b>WhatsApp :</b> +212 6 00 00 00 00</div>
        <div><b>E-mail :</b> contact@votresite.com</div>
      </div>

      <h2 className="c-sub">Restons en contact</h2>

      {ok && <div className="c-success">{ok}</div>}
      {err && <div className="c-error">{err}</div>}

      <form className="c-form" onSubmit={submit}>
        <div className="c-grid">
          <input
            className="c-input"
            placeholder="Nom *"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            className="c-input"
            placeholder="E-mail *"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <input
          className="c-input"
          placeholder="Téléphone (optionnel)"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
        <textarea
          className="c-textarea"
          placeholder="Message *"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button className="c-btn" disabled={sending}>
          {sending ? "Envoi…" : "Envoyer"}
        </button>
      </form>
    </div>
  );
}
