// src/pages/ProductCreatePage.js
import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Form, Button } from 'react-bootstrap';
import { createProduct } from '../actions/productActions';
import { useHistory } from 'react-router';
import { checkTokenValidation, logout } from '../actions/userActions';
import { CREATE_PRODUCT_RESET } from '../constants';
import Message from '../components/Message';

const ProductCreatePage = () => {
  let history = useHistory();
  const dispatch = useDispatch();

  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [stock, setStock] = useState(false);
  const [image, setImage] = useState(null);

  // ➕ NEW: category + variants (sizes)
  const [category, setCategory] = useState("other");
  const [variants, setVariants] = useState([{ label: "", size_ml: "", price: "", in_stock: true, sku: "" }]);

  const addVariantRow = () =>
    setVariants(v => [...v, { label: "", size_ml: "", price: "", in_stock: true, sku: "" }]);

  const removeVariantRow = (idx) =>
    setVariants(v => v.filter((_, i) => i !== idx));

  const changeVariant = (idx, field, value) =>
    setVariants(v => v.map((row, i) => (i === idx ? { ...row, [field]: value } : row)));

  // reducers
  const { userInfo } = useSelector(state => state.userLoginReducer);
  const { product, success: productCreationSuccess, error: productCreationError } =
    useSelector(state => state.createProductReducer);
  const { error: tokenError } = useSelector(state => state.checkTokenValidationReducer);

  useEffect(() => {
    if (!userInfo) history.push("/login");
    dispatch(checkTokenValidation());
  }, [dispatch, userInfo, history]);

  const onSubmit = (e) => {
    e.preventDefault();

    const form_data = new FormData();
    form_data.append('name', name);
    form_data.append('description', description);
    form_data.append('price', price);
    form_data.append('stock', stock);
    form_data.append('image', image);
    form_data.append('category', category);

    // only send non-empty variants
    const clean = variants
      .filter(v => v.label && v.price !== "")
      .map(v => ({
        label: v.label,
        size_ml: v.size_ml === "" ? null : Number(v.size_ml),
        price: Number(v.price),
        in_stock: !!v.in_stock,
        sku: v.sku || "",
      }));
    if (clean.length) {
      form_data.append('variants', JSON.stringify(clean));
    }

    dispatch(createProduct(form_data));
  };

  if (productCreationSuccess) {
    alert("Product successfully created.");
    history.push(`/product/${product.id}/`);
    dispatch({ type: CREATE_PRODUCT_RESET });
  }

  if (userInfo && tokenError === "Request failed with status code 401") {
    alert("Session expired, please login again.");
    dispatch(logout());
    history.push("/login");
    window.location.reload();
  }

  return (
    <div>
      {productCreationError && (
        <Message variant='danger'>
          {productCreationError.image ? productCreationError.image[0] : String(productCreationError)}
        </Message>
      )}

      <span className="d-flex justify-content-center text-info">
        <em>New Product</em>
      </span>

      <Form onSubmit={onSubmit}>
        {/* Name */}
        <Form.Group controlId='name'>
          <Form.Label><b>Product Name</b></Form.Label>
          <Form.Control
            required
            autoFocus
            type="text"
            value={name}
            placeholder="product name"
            onChange={(e) => setName(e.target.value)}
          />
        </Form.Group>

        {/* Description */}
        <Form.Group controlId='description'>
          <Form.Label><b>Product Description</b></Form.Label>
          <Form.Control
            required
            type="text"
            value={description}
            placeholder="product description"
            onChange={(e) => setDescription(e.target.value)}
          />
        </Form.Group>

        {/* Base price (fallback if no variants) */}
        <Form.Group controlId='price'>
          <Form.Label><b>Price (base)</b></Form.Label>
          <Form.Control
            required
            type="number"
            step="0.01"
            pattern="[0-9]+(\.[0-9]{1,2})?%?"
            value={price}
            placeholder="199.99"
            maxLength="8"
            onChange={(e) => setPrice(e.target.value)}
          />
        </Form.Group>

        {/* In stock */}
        <div className="d-flex align-items-center mb-3">
          <Form.Label className="mb-0">In Stock</Form.Label>
          <Form.Check
            type="checkbox"
            className="ml-2"
            checked={stock}
            onChange={() => setStock(!stock)}
          />
        </div>

        {/* Image */}
        <Form.Group controlId='image'>
          <Form.Label><b>Product Image</b></Form.Label>
          <Form.Control
            required
            type="file"
            onChange={(e) => setImage(e.target.files[0])}
          />
        </Form.Group>

        {/* ➕ Category */}
        <Form.Group controlId='category'>
          <Form.Label><b>Category</b></Form.Label>
          <Form.Control
            as="select"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value="face">Face</option>
            <option value="lips">Lips</option>
            <option value="eyes">Eyes</option>
            <option value="eyebrow">Eyebrow</option>
            <option value="hair">Hair</option>
            <option value="other">Other</option>
          </Form.Control>
        </Form.Group>

        {/* ➕ Variants (sizes) */}
        <Form.Group>
          <Form.Label><b>Sizes / Variants (optional)</b></Form.Label>

          {variants.map((row, i) => (
            <div key={i} className="d-flex align-items-center mb-2" style={{ gap: 8 }}>
              <Form.Control
                style={{ maxWidth: 240 }}
                placeholder="Label (e.g. 500 ml)"
                value={row.label}
                onChange={(e) => changeVariant(i, "label", e.target.value)}
              />
              <Form.Control
                type="number"
                style={{ maxWidth: 120 }}
                placeholder="Size ml (opt.)"
                value={row.size_ml}
                onChange={(e) => changeVariant(i, "size_ml", e.target.value)}
              />
              <Form.Control
                type="number"
                step="0.01"
                style={{ maxWidth: 140 }}
                placeholder="Price"
                value={row.price}
                onChange={(e) => changeVariant(i, "price", e.target.value)}
              />
              <Form.Check
                className="ml-2"
                label="In stock"
                checked={row.in_stock}
                onChange={(e) => changeVariant(i, "in_stock", e.target.checked)}
              />
              <Form.Control
                style={{ maxWidth: 140 }}
                placeholder="SKU (opt.)"
                value={row.sku}
                onChange={(e) => changeVariant(i, "sku", e.target.value)}
              />
              <Button
                variant="outline-danger"
                size="sm"
                className="ml-2"
                onClick={() => removeVariantRow(i)}
              >
                Remove
              </Button>
            </div>
          ))}

          <Button
            variant="outline-secondary"
            size="sm"
            className="mt-2"
            onClick={addVariantRow}
          >
            + Add size
          </Button>
        </Form.Group>

        <Button type="submit" variant='success' className="btn-sm button-focus-css">
          Save Product
        </Button>
        <Button
          type="button"
          variant='primary'
          className="btn-sm ml-2 button-focus-css"
          onClick={() => history.push("/")}
        >
          Cancel
        </Button>
      </Form>
    </div>
  );
};

export default ProductCreatePage;
