import React, { useState } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";
import "./login-register.css";

export default function ResetPasswordPage() {
  const { uid, token } = useParams();
  const [pw1, setPw1] = useState("");
  const [pw2, setPw2] = useState("");
  const [ok, setOk] = useState(false);
  const [err, setErr] = useState("");

  const submit = async (e) => {
    e.preventDefault();
    setErr("");
    if (pw1.length < 8) return setErr("Le mot de passe doit contenir au moins 8 caractères.");
    if (pw1 !== pw2) return setErr("Les mots de passe ne correspondent pas.");
    try {
      await axios.post("/account/password-reset-confirm/", { uid, token, new_password: pw1 });
      setOk(true);
    } catch (e) {
      setErr(e?.response?.data?.detail || "Lien invalide ou expiré.");
    }
  };

  return (
    <div className="auth-wrap">
      <div className="auth-card">
        <div className="auth-side">
          <img src="/brand/logop.png" alt="MiniGlow" className="auth-logo" />
          <h2 className="auth-headline">Réinitialisez votre mot de passe</h2>
          <ul className="auth-bullets">
            <li>Au moins 8 caractères</li>
            <li>Évitez de réutiliser un ancien mot de passe</li>
          </ul>
        </div>

        <div className="auth-form">
          <h1 className="auth-title">Nouveau mot de passe</h1>

          {ok ? (
            <div className="alert success">
              Mot de passe mis à jour ✅&nbsp;
              Vous pouvez maintenant <Link to="/login">vous connecter</Link>.
            </div>
          ) : (
            <form onSubmit={submit} className="auth-form-inner">
              {err && <div className="alert danger">{err}</div>}

              <label>Nouveau mot de passe</label>
              <input type="password" value={pw1} onChange={(e)=>setPw1(e.target.value)} placeholder="••••••••" />

              <label>Confirmer le mot de passe</label>
              <input type="password" value={pw2} onChange={(e)=>setPw2(e.target.value)} placeholder="••••••••" />

              <button className="auth-btn-primary" type="submit">Mettre à jour</button>
              <div className="auth-switch">
                Retour à la connexion ? <Link to="/login">Se connecter</Link>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
}
