// src/pages/ForgotPasswordPage.jsx
import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./login-register.css";

export default function ForgotPasswordPage() {
  const [email, setEmail] = useState("");
  const [ok, setOk] = useState(false);
  const [err, setErr] = useState("");

  const submit = async (e) => {
    e.preventDefault();
    setErr("");
    try {
      await axios.post("/account/password-reset/", { email });
      setOk(true);
    } catch (e) {
      setErr(e?.response?.data?.detail || "Une erreur est survenue.");
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-card">
        <div className="auth-side">
          <img src="/brand/logop.png" alt="MiniGlow" className="auth-logo" />
          <h2 className="auth-headline">Réinitialiser votre mot de passe</h2>
          <p className="auth-blurb">Nous vous enverrons un lien sécurisé par email.</p>
        </div>

        <div className="auth-form">
          <h1 className="auth-title">Mot de passe oublié</h1>
          {ok ? (
            <div className="alert success">
              Si un compte existe pour <b>{email}</b>, un lien a été envoyé.  
              <div className="mt-2"><Link to="/login">Retour à la connexion</Link></div>
            </div>
          ) : (
            <form onSubmit={submit} className="auth-form-inner">
              {err && <div className="alert danger">{err}</div>}
              <label>Votre adresse email</label>
              <input
                type="email"
                value={email}
                placeholder="vous@example.com"
                onChange={(e)=>setEmail(e.target.value)}
                required
              />
              <button type="submit" className="auth-btn-primary">Envoyer le lien</button>
              <div className="auth-switch">
                <Link to="/login">Retour à la connexion</Link>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
}
