// src/pages/AdminShippingRatesPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useSelector } from "react-redux";
import { Container, Row, Col, Table, Form, Button, InputGroup } from "react-bootstrap";
import Message from "../components/Message";

const AdminShippingRatesPage = ({ history }) => {
  const { userInfo } = useSelector((s) => s.userLoginReducer || {});
  const [items, setItems] = useState([]);
  const [q, setQ] = useState("");
  const [form, setForm] = useState({ city: "", price: "", active: true });
  const [error, setError] = useState(null);
  const [saving, setSaving] = useState(false);

  const auth = userInfo?.token ? { Authorization: `Bearer ${userInfo.token}` } : {};

  const load = async () => {
    try {
      const { data } = await axios.get(`/api/admin/shipping-rates/?q=${encodeURIComponent(q)}`, { headers: auth });
      setItems(data);
    } catch (e) {
      setError(e?.response?.data?.detail || e.message);
    }
  };

  useEffect(() => {
    if (!userInfo?.admin) {
      history.push("/login");
      return;
    }
    load();
    // eslint-disable-next-line
  }, [q, userInfo]);

  const onChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((f) => ({ ...f, [name]: type === "checkbox" ? checked : value }));
  };

  const create = async (e) => {
    e.preventDefault();
    setSaving(true);
    try {
      const { data } = await axios.post("/api/admin/shipping-rates/", form, { headers: { "Content-Type": "application/json", ...auth } });
      setForm({ city: "", price: "", active: true });
      setItems((prev) => [data, ...prev]);
    } catch (e) {
      setError(e?.response?.data?.detail || e.message);
    } finally {
      setSaving(false);
    }
  };

  const update = async (id, patch) => {
    try {
      const { data } = await axios.put(`/api/admin/shipping-rates/${id}/`, patch, { headers: { "Content-Type": "application/json", ...auth } });
      setItems((arr) => arr.map((x) => (x.id === id ? data : x)));
    } catch (e) {
      setError(e?.response?.data?.detail || e.message);
    }
  };

  const remove = async (id) => {
    if (!window.confirm("Supprimer ce tarif ?")) return;
    try {
      await axios.delete(`/api/admin/shipping-rates/${id}/`, { headers: auth });
      setItems((arr) => arr.filter((x) => x.id !== id));
    } catch (e) {
      setError(e?.response?.data?.detail || e.message);
    }
  };

  return (
    <Container className="py-4">
      <Row className="mb-3">
        <Col><h2>Admin — Tarifs de Livraison</h2></Col>
        <Col md="4">
          <InputGroup>
            <InputGroup.Text>Recherche</InputGroup.Text>
            <Form.Control placeholder="Ville..." value={q} onChange={(e) => setQ(e.target.value)} />
          </InputGroup>
        </Col>
      </Row>

      {error && <Message variant="danger">{error}</Message>}

      <Row className="mb-4">
        <Col md={6}>
          <Form onSubmit={create} className="card-soft p-3">
            <h5 className="mb-3">Ajouter un tarif</h5>
            <Form.Group className="mb-2">
              <Form.Label>Ville</Form.Label>
              <Form.Control name="city" value={form.city} onChange={onChange} required />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Prix (DH)</Form.Label>
              <Form.Control name="price" type="number" step="0.01" value={form.price} onChange={onChange} required />
            </Form.Group>

            <Form.Check
              type="checkbox"
              className="mb-3"
              label="Actif"
              name="active"
              checked={form.active}
              onChange={onChange}
            />

            <Button type="submit" disabled={saving}>Enregistrer</Button>
          </Form>
        </Col>
      </Row>

      <Row>
        <Col>
          <Table bordered hover responsive>
            <thead>
              <tr>
                <th>Ville</th>
                <th style={{ width: 160 }}>Prix (DH)</th>
                <th style={{ width: 120 }}>Actif</th>
                <th style={{ width: 140 }} />
              </tr>
            </thead>
            <tbody>
              {items.map((it) => (
                <tr key={it.id}>
                  <td>{it.city}</td>
                  <td>
                    <Form.Control
                      type="number"
                      step="0.01"
                      defaultValue={it.price}
                      onBlur={(e) => update(it.id, { price: e.target.value })}
                    />
                  </td>
                  <td className="text-center">
                    <Form.Check
                      type="switch"
                      defaultChecked={it.active}
                      onChange={(e) => update(it.id, { active: e.target.checked })}
                    />
                  </td>
                  <td className="text-end">
                    <Button size="sm" variant="outline-danger" onClick={() => remove(it.id)}>
                      Supprimer
                    </Button>
                  </td>
                </tr>
              ))}
              {!items.length && (
                <tr>
                  <td colSpan="4" className="text-center text-muted py-4">Aucun tarif pour l’instant.</td>
                </tr>
              )}
            </tbody>
          </Table>
        </Col>
      </Row>
    </Container>
  );
};

export default AdminShippingRatesPage;
