// src/pages/HomePage.js
import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Carousel, Button, Spinner, Alert } from 'react-bootstrap';
import axios from 'axios';
import Product from '../components/Product';

const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading]   = useState(true);
  const [error, setError]       = useState(null);

  useEffect(() => {
    let ok = true;
    (async () => {
      try {
        const { data } = await axios.get('/api/products/');
        if (ok) setProducts((data || []).slice(0, 8));
      } catch (e) {
        if (ok) setError(e?.response?.data?.detail || e.message);
      } finally {
        if (ok) setLoading(false);
      }
    })();
    return () => { ok = false; };
  }, []);

  const banners = [
    '/hero/banner1.jpg',
    '/hero/banner2.jpg',
    '/hero/banner3.jpg',
    '/hero/banner4.jpg',
    '/hero/banner5.jpg'
  ];

  return (
    <>
      {/* Hero with welcome overlay */}
      <section className="hero-wrapper">
  <Carousel interval={4000} pause="hover" fade={false} slide={true}>
    {banners.map((src, i) => (
      <Carousel.Item key={i}>
        <img className="d-block w-100 hero-img" src={src} alt={`Banner ${i+1}`} />
      </Carousel.Item>
    ))}
  </Carousel>

  {/* Overlay welcome text */}
  <div className="hero-overlay text-center text-white">
    <h1 className="display-4 mb-3">Welcome to Your K-Beauty Shop</h1>
    <p className="lead mb-4">
      Safe, effective routines for hydration, brightening and barrier repair â€” shipped fast.
    </p>
    <Button size="lg" variant="light" href="/products">Shop Now</Button>
  </div>
</section>


      {/* Featured products */}
      <Container className="py-5">
        <Row className="align-items-center mb-3">
          <Col><h4 className="m-0">Featured this week</h4></Col>
          <Col className="text-right">
            <Button variant="outline-secondary" size="sm" href="/products">See all</Button>
          </Col>
        </Row>

        {loading && <div className="d-flex justify-content-center py-5"><Spinner animation="border" /></div>}
        {error && <Alert variant="danger">{error}</Alert>}

        <Row>
          {products.map(p => (
            <Col key={p.id} xs={12} sm={6} md={4} lg={3} className="mb-4">
              <Product product={p} />
            </Col>
          ))}
          {!loading && !error && products.length === 0 && (
            <Col className="text-center text-muted py-5">No featured products yet.</Col>
          )}
        </Row>
      </Container>
    </>
  );
};

export default HomePage;
