// src/pages/HomePage.js
import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Button, Spinner, Alert } from 'react-bootstrap';
import axios from 'axios';
import "../components/HomeProducts.css";
import HomeProductCard from "../components/HomeProductCard";

const HomePage = () => {
  const [products, setProducts]   = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);

  useEffect(() => {
    let ok = true;
    (async () => {
      try {
        const { data } = await axios.get('/api/products/');
        if (ok) setProducts((data || []).slice(0, 8));
      } catch (e) {
        if (ok) setError(e?.response?.data?.detail || e.message);
      } finally {
        if (ok) setLoading(false);
      }
    })();
    return () => { ok = false; };
  }, []);

  const banners = [
    '/hero/banner1.jpg',
    '/hero/banner2.jpg',
    '/hero/banner3.jpg',
    '/hero/banner4.jpg',
    '/hero/banner5.jpg',
  ];

  return (
    <>
      {/* HERO: images stuck together, auto-moving to the right */}
      <section className="hero-strip">
        {/* overlay welcome text */}
        <div className="hero-overlay text-center text-white">
          <h1 className="display-4 mb-3">Welcome to Your Beauty Shop</h1>
          <p className="lead mb-4">
            Safe, effective routines for hydration, brightening and barrier repair â€” shipped fast.
          </p>
          <Button size="lg" variant="light" href="/products">Discover our products</Button>
        </div>

        {/* moving track (duplicate sequence for seamless loop) */}
        <div className="hero-track">
          {banners.map((src, i) => (
            <img key={`a-${i}`} className="hero-tile" src={src} alt={`banner ${i + 1}`} />
          ))}
          {banners.map((src, i) => (
            <img key={`b-${i}`} className="hero-tile" src={src} alt={`banner dup ${i + 1}`} />
          ))}
        </div>
      </section>

      {/* Featured products */}
      <Container className="py-5">
        <Row className="align-items-center mb-3">
          <Col>
            <h2 className="m-0" style={{ fontWeight: 900 }}>Featured this week</h2>
          </Col>
          <Col className="text-right">
            <Button variant="outline-secondary" size="sm" href="/products">
              See all
            </Button>
          </Col>
        </Row>

        {loading && (
          <div className="d-flex justify-content-center py-5">
            <Spinner animation="border" />
          </div>
        )}
        {error && <Alert variant="danger">{error}</Alert>}

        {/* NEW glossy grid (scoped styles) */}
        {!loading && !error && (
          <>
            {products.length > 0 ? (
              <div className="hp-grid">
                {products.map((p) => (
                  <HomeProductCard key={p.id} product={p} />
                ))}
              </div>
            ) : (
              <div className="text-center text-muted py-5">
                No featured products yet.
              </div>
            )}
          </>
        )}
      </Container>
    </>
  );
};

export default HomePage;
