import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";
import "./brand-marquee.css";

const LOGOS = {
  "beauty of joseon": "/brands/boj.svg",
  "axis-y": "/brands/axisy.svg",
  "cosrx": "/brands/cosrx.svg",
  "skin1004": "/brands/skin1004.svg",
  "dr. althea": "/brands/dralthea.svg",
  "i'm from": "/brands/imfrom.svg",
  "anua": "/brands/anua.svg",
};

function Track({ brands, ariaHidden }) {
  return (
    <div className={`bm-track${ariaHidden ? " bm-track--2" : ""}`} aria-hidden={ariaHidden || undefined}>
      {brands.map((name, i) => {
        const key = String(name || "").trim().toLowerCase();
        const src = LOGOS[key];
        return (
          <div className="bm-card" key={`${name}-${i}`}>
            {src ? <img src={src} alt={name} /> : <span className="bm-text">{name}</span>}
          </div>
        );
      })}
    </div>
  );
}

export default function BrandMarquee() {
  const [brands, setBrands] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let ok = true;
    (async () => {
      try {
        const { data } = await axios.get("/api/brands/");
        if (ok) setBrands(Array.isArray(data) ? data : []);
      } finally {
        if (ok) setLoading(false);
      }
    })();
    return () => { ok = false; };
  }, []);

  // Repeat once so each track is at least 100% width for a smooth loop
  const display = useMemo(() => [...brands, ...brands], [brands]);

  if (loading || !display.length) return null;

  return (
    <section className="bm-wrap">
  <h2 className="bm-title">Toutes nos marques</h2>

  <div className="bm-rail">
    <div className="bm-track">
      {brands.map((b, i) => (
        <div className="bm-card" key={`b-${i}`}>
          {logos[b] ? (
            <img src={logos[b]} alt={b} />
          ) : (
            <span className="bm-text">{b}</span>
          )}
        </div>
      ))}

      {/* duplicate for seamless infinite scroll */}
      {brands.map((b, i) => (
        <div className="bm-card" key={`b2-${i}`}>
          {logos[b] ? (
            <img src={logos[b]} alt={b} />
          ) : (
            <span className="bm-text">{b}</span>
          )}
        </div>
      ))}
    </div>
  </div>
</section>

  );
}
