import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Form, Button, Row, Col } from 'react-bootstrap';
import { register } from '../actions/userActions';
import Message from '../components/Message';

function RegisterPage({ history }) {
  const [username, setUsername] = useState("");
  const [email, setEmail]       = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage]   = useState("");

  const dispatch = useDispatch();
  const { loading, error, success } = useSelector((s) => s.userRegisterReducer || {});

  useEffect(() => {
    if (success) {
      // ✅ after successful register, send to Login (no auto-login)
      history.push('/login');
    }
  }, [success, history]);

  const submitHandler = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setMessage('Passwords do not match!');
      return;
    }
    setMessage("");
    dispatch(register(username, email, password));
  };

  return (
    <div>
      <Row className='justify-content-md-center'>
        <Col xs={12} md={6}>
          <h1>Sign Up</h1>
          {message && <Message variant='danger'>{message}</Message>}
          {error && <Message variant='danger'>{error}</Message>}

          <Form onSubmit={submitHandler}>
            <Form.Group controlId='name'>
              <Form.Label>Username</Form.Label>
              <Form.Control
                required type="text" placeholder="enter your username"
                value={username} onChange={(e) => setUsername(e.target.value)}
              />
            </Form.Group>

            <Form.Group controlId='email'>
              <Form.Label>Email Address</Form.Label>
              <Form.Control
                required type="email" placeholder="enter your email"
                value={email} onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>

            <Form.Group controlId='password'>
              <Form.Label>Password</Form.Label>
              <Form.Control
                required type="password" placeholder="enter your password"
                value={password} onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>

            <Form.Group controlId='passwordConfirm'>
              <Form.Label>Confirm Password</Form.Label>
              <Form.Control
                required type="password" placeholder="confirm your password"
                value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)}
              />
            </Form.Group>

            <Button type="submit" variant='primary' disabled={loading}>
              {loading ? 'Creating…' : 'Sign Up'}
            </Button>
          </Form>

          <Row className="py-3">
            <Col>
              Already have an account? <Link to="/login">Login</Link>
            </Col>
          </Row>
        </Col>
      </Row>
    </div>
  );
}

export default RegisterPage;
