// src/components/BrandBar.js
import React, { useEffect, useMemo, useRef, useState } from 'react';
import { Container, Dropdown, Form, InputGroup, Button } from 'react-bootstrap';
import { Link, useHistory } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { logout } from '../actions/userActions';

/* ---------- Small inline search popover ---------- */
function BrandSearch() {
  const [open, setOpen] = useState(false);
  const [q, setQ] = useState('');
  const history = useHistory();

  const submit = (e) => {
    e.preventDefault();
    const needle = q.trim();
    if (!needle) return;
    history.push(`/products?search=${encodeURIComponent(needle.toLowerCase())}`);
    setOpen(false);
  };

  return (
    <div className="brand-search">
      {!open ? (
        <Button
          variant="link"
          className="icon-link p-0"
          onClick={() => setOpen(true)}
          aria-label="Open search"
          title="Recherche"
        >
          <i className="fas fa-search" />
        </Button>
      ) : (
        <Form onSubmit={submit} className="brand-search-form">
          <InputGroup size="sm">
            <Form.Control
              autoFocus
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="Rechercher un produit…"
              className="brand-search-input"
            />
            <Button type="submit" variant="dark" className="brand-search-btn">
              <i className="fas fa-search" />
            </Button>
            <Button
              variant="link"
              className="brand-search-close"
              onClick={() => setOpen(false)}
              aria-label="Close search"
            >
              <i className="fas fa-times" />
            </Button>
          </InputGroup>
        </Form>
      )}
    </div>
  );
}

/* ---------- Admin bell (unread contact messages) ---------- */
function AdminBell() {
  const { userInfo } = useSelector((s) => s.userLoginReducer || {});
  const isAdmin = !!(userInfo && userInfo.admin);

  const [count, setCount] = useState(0);
  const [recent, setRecent] = useState([]);
  const [loading, setLoading] = useState(false);
  const timerRef = useRef(null);

  const fetchAll = async () => {
    if (!isAdmin) return;
    try {
      setLoading(true);
      const [c, r] = await Promise.all([
        axios.get('/api/admin/contact/unread-count/'),
        axios.get('/api/admin/contact/recent/?limit=5'),
      ]);
      setCount(Number(c?.data?.count || 0));
      setRecent(Array.isArray(r?.data) ? r.data : []);
    } catch (_) {
      // silently ignore (keep old values)
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (!isAdmin) return;
    fetchAll();
    timerRef.current = setInterval(fetchAll, 30000); // every 30s
    return () => clearInterval(timerRef.current);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isAdmin]);

  if (!isAdmin) return null;

  const adminChangeUrl = (id) => `/admin/contact/contactmessage/${id}/change/`;

  return (
    <Dropdown align="end">
      <Dropdown.Toggle
        as="button"
        className="icon-link btn btn-link p-0 brand-user-toggle"
        aria-label="Notifications"
        title="Notifications"
      >
        <i className="fas fa-bell" />
        {count > 0 && <span className="icon-badge">{count}</span>}
      </Dropdown.Toggle>

      <Dropdown.Menu className="brand-user-menu" style={{ minWidth: 320 }}>
        <Dropdown.Header>Nouveaux messages</Dropdown.Header>

        {loading && <div className="px-3 py-2 text-muted">Chargement…</div>}

        {!loading && recent.length === 0 && (
          <div className="px-3 py-2 text-muted">Aucun nouveau message</div>
        )}

        {recent.map((m) => (
          <Dropdown.Item
            key={m.id}
            as="a"
            href={adminChangeUrl(m.id)}
            target="_blank"
            rel="noreferrer"
          >
            <div className="d-flex flex-column">
              <strong className="text-truncate">{m.name} <small className="text-muted">&lt;{m.email}&gt;</small></strong>
              <small className="text-muted text-truncate">{m.preview}</small>
            </div>
          </Dropdown.Item>
        ))}

        <Dropdown.Divider />
        <Dropdown.Item as="a" href="/admin/contact/contactmessage/" target="_blank" rel="noreferrer">
          Ouvrir l’administration
        </Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  );
}

/* ---------- Top brand bar ---------- */
export default function BrandBar() {
  const dispatch = useDispatch();
  const history = useHistory();

  const { userInfo } = useSelector((s) => s.userLoginReducer || {});
  // wishlist badge
  const { items: wishlistItems = [] } = useSelector((s) => s.wishlist || {});
  const wishCount = wishlistItems.length || 0;

  // cart badge (adapt to your cart slice; fallback to 0)
  const cartItems = useSelector((s) => (s.cart && s.cart.items) || []);
  const cartCount =
    cartItems?.reduce?.((sum, it) => sum + (Number(it.qty) || 1), 0) ||
    cartItems?.length ||
    0;

  const logoutHandler = () => {
    dispatch(logout());
    history.push('/login');
    window.location.reload();
  };

  return (
    <div className="brandbar">
      <Container className="brandbar-inner">
        {/* Left spacer */}
        <div className="brandbar-left" />

        {/* Centered logo */}
        <div className="brandbar-logo">
          <Link to="/">
            <img src="/brand/logob.svg" alt="Beauty Shop" className="brand-logo-img" />
          </Link>
        </div>

        {/* Right icons */}
        <div className="brandbar-icons">
          {/* Search */}
          <BrandSearch />

          {/* Wishlist */}
          <Link to="/wishlist" title="Wishlist" className="icon-link" style={{ position: 'relative' }}>
            <i className="fas fa-heart" />
            {wishCount > 0 && <span className="icon-badge">{wishCount}</span>}
          </Link>

          {/* Cart (Panier) */}
          <Link to="/cart" title="Panier" className="icon-link" style={{ position: 'relative' }}>
            <i className="fas fa-shopping-bag" />
            {cartCount > 0 && <span className="icon-badge">{cartCount}</span>}
          </Link>

          {/* Admin bell (only for admins) */}
          <AdminBell />

          {/* User dropdown */}
          {userInfo ? (
            <Dropdown align="end">
              <Dropdown.Toggle
                as="button"
                className="icon-link btn btn-link p-0 brand-user-toggle"
                aria-label="User menu"
              >
                <i className="fas fa-user" />
              </Dropdown.Toggle>

              <Dropdown.Menu className="brand-user-menu">
                <Dropdown.Header className="text-capitalize">
                  {userInfo.username}
                </Dropdown.Header>

                <Dropdown.Item as={Link} to="/account">
                  Account Settings
                </Dropdown.Item>
                <Dropdown.Item as={Link} to="/all-orders/">
                  All Orders
                </Dropdown.Item>

                {userInfo.admin && (
                  <>
                    <Dropdown.Divider />
                    <Dropdown.Item as={Link} to="/new-product/">
                      Add Product
                    </Dropdown.Item>
                  </>
                )}

                <Dropdown.Divider />
                <Dropdown.Item onClick={logoutHandler}>Logout</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          ) : (
            <Link to="/login" title="Login" className="icon-link">
              <i className="fas fa-user" />
            </Link>
          )}
        </div>
      </Container>
    </div>
  );
}
