// src/pages/ProductDetailsPage.js
import React, { useEffect, useMemo, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Spinner, Row, Col, Container, Button, Modal } from 'react-bootstrap';
import Message from '../components/Message';
import { deleteProduct, getProductDetails } from '../actions/productActions';
import { toggleWishlist } from '../actions/wishlistActions';

import {
  CREATE_PRODUCT_RESET,
  DELETE_PRODUCT_RESET,
  UPDATE_PRODUCT_RESET,
  CARD_CREATE_RESET,
} from '../constants';

function ProductDetailsPage({ history, match }) {
  const dispatch = useDispatch();

  // modal
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow  = () => setShow(true);

  // qty + variant
  const [qty, setQty] = useState(1);
  const [variantId, setVariantId] = useState(null); // selected variant id

  // data
  const { loading, error, product } = useSelector((s) => s.productDetailsReducer);
  const { userInfo } = useSelector((s) => s.userLoginReducer);
  const { success: productDeletionSuccess } = useSelector((s) => s.deleteProductReducer);
  const { items: wishlistItems = [] } = useSelector((s) => s.wishlist || {});

  const pid = product?.id;
  const inWishlist = pid ? wishlistItems.some((w) => (w.id || w._id) === pid) : false;

  // compute active variant + price
  const variants = product?.variants || [];
  const activeVariant = useMemo(
    () => variants.find(v => String(v.id) === String(variantId)) || null,
    [variants, variantId]
  );
  const unitPrice = activeVariant ? Number(activeVariant.price) : Number(product?.price || 0);
  const total = unitPrice * qty;

  // qty handlers
  const plus  = () => setQty((q) => Math.min(99, q + 1));
  const minus = () => setQty((q) => Math.max(1, q - 1));

  // wishlist
  const handleToggleWishlist = () => {
    if (!pid) return;
    dispatch(toggleWishlist(pid));
  };

  // WhatsApp order (with variant + qty)
  const openOrder = () => {
    const phone = "212600000000"; // <- your admin WhatsApp in intl format, NO '+'
    const variantText = activeVariant ? ` ‚Äì ${activeVariant.label}` : "";
    const msg = `Bonjour! Je souhaite commander:
‚Ä¢ ${product?.name}${variantText}
‚Ä¢ Quantit√©: ${qty}
‚Ä¢ Total estim√©: ${new Intl.NumberFormat('fr-MA',{style:'currency',currency:'MAD'}).format(total)}

Nom:
Pr√©nom:
T√©l√©phone:
Adresse:`;
    const url = `https://wa.me/${phone}?text=${encodeURIComponent(msg)}`;
    window.open(url, "_blank", "noopener");
  };

  useEffect(() => {
    dispatch(getProductDetails(match.params.id));
    dispatch({ type: UPDATE_PRODUCT_RESET });
    dispatch({ type: CREATE_PRODUCT_RESET });
    dispatch({ type: CARD_CREATE_RESET });
  }, [dispatch, match]);

  useEffect(() => {
    // default select first in-stock variant if exists
    if (variants.length && variantId == null) {
      const firstOk = variants.find(v => v.in_stock) || variants[0];
      setVariantId(firstOk.id);
    }
  }, [variants, variantId]);

  const confirmDelete = () => {
    dispatch(deleteProduct(match.params.id));
    handleClose();
  };

  if (productDeletionSuccess) {
    alert('Product successfully deleted.');
    history.push('/');
    dispatch({ type: DELETE_PRODUCT_RESET });
  }

  return (
    <div>
      {/* Modal */}
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>
            <i style={{ color: '#e6e600' }} className="fas fa-exclamation-triangle" /> Delete Confirmation
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete this product <em>"{product?.name}"</em>?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="danger" onClick={confirmDelete}>Confirm Delete</Button>
          <Button variant="primary" onClick={handleClose}>Cancel</Button>
        </Modal.Footer>
      </Modal>

      {loading && (
        <span style={{ display: 'flex' }}>
          <h5>Getting Product Details</h5>
          <span className="ml-2"><Spinner animation="border" /></span>
        </span>
      )}

      {error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Container className="pd">
          <Row className="gy-4">
            {/* LEFT ‚Äì image */}
            <Col lg={6}>
              <div className="pd-media">
                <div className="pd-media-frame">
                  <img src={product?.image} alt={product?.name} />
                </div>
              </div>
            </Col>

            {/* RIGHT ‚Äì info */}
            <Col lg={6}>
              <div className="pd-card mb-3">
                <h2 className="pd-title">{product?.name}</h2>

                <div className="pd-subtle mb-3">
                  {product?.category ? <>Cat√©gorie : <b>{product.category}</b></> : null}
                </div>

                <div className="mb-3">
                  {product?.stock ? (
                    <span className="pd-chip pd-chip--ok"><i className="fas fa-check-circle" /> En stock</span>
                  ) : (
                    <span className="pd-chip pd-chip--ko"><i className="fas fa-times-circle" /> Out of stock</span>
                  )}
                </div>

                {product?.description && (
                  <p className="pd-subtle" style={{ lineHeight: 1.7 }}>{product.description}</p>
                )}

                {/* ===== VARIANTS (styled pills) ===== */}
                {variants.length > 0 && (
                  <div className="mb-3">
                    <div className="pd-subtle mb-2">Taille / Format</div>
                    <div className="pd-variants">
                      {variants.map((v) => (
                        <button
                          key={v.id}
                          type="button"
                          className="pd-variant-pill"
                          aria-pressed={String(variantId) === String(v.id)}
                          disabled={!v.in_stock}
                          onClick={() => setVariantId(v.id)}
                          title={!v.in_stock ? "Indisponible" : v.label}
                          /* price bubble on hover */
                          data-price={new Intl.NumberFormat('fr-MA', { style: 'currency', currency: 'MAD' }).format(Number(v.price))}
                        >
                          {v.label}
                        </button>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              {/* price + qty + ctas */}
              <div className="pd-card">
                <div className="pd-price mb-3">
                  <span className="pd-subtle">Prix</span>
                  <strong>
                    {new Intl.NumberFormat('fr-MA', { style: 'currency', currency: 'MAD' })
                      .format(unitPrice)}
                  </strong>
                </div>

                <div className="mb-3">
                  <div className="pd-subtle mb-2">Quantit√©</div>
                  <div className="pd-qty">
                    <button type="button" className="btn" onClick={minus} aria-label="minus">&minus;</button>
                    <input
                      value={qty}
                      onChange={(e) => {
                        const v = Number(e.target.value);
                        if (!Number.isNaN(v)) setQty(Math.max(1, Math.min(99, v)));
                      }}
                    />
                    <button type="button" className="btn" onClick={plus} aria-label="plus">+</button>
                  </div>
                </div>

                <div className="pd-actions mb-2">
                  <button className="pd-btn-primary" onClick={openOrder}>
                    <i className="fab fa-whatsapp mr-2" /> Commander maintenant
                  </button>

                  {product?.stock ? (
                    <Link to={`${product.id}/checkout/`} className="pd-btn-outline btn">
                      Pay with Stripe
                    </Link>
                  ) : null}

                  <button
                    onClick={handleToggleWishlist}
                    className="pd-btn-outline"
                    aria-label={inWishlist ? 'Retirer de la wishlist' : 'Ajouter √† la wishlist'}
                  >
                    {inWishlist ? 'üíî Retirer de ma wishlist' : 'ü§ç Ajouter √† ma wishlist'}
                  </button>

                  {userInfo && userInfo.admin && (
                    <>
                      <button
                        className="pd-btn-outline"
                        onClick={() => history.push(`/product-update/${product.id}/`)}
                      >
                        Edit Product
                      </button>
                      <button className="pd-btn-outline" onClick={handleShow}>
                        Delete Product
                      </button>
                    </>
                  )}
                </div>

                <div className="pd-total">
                  Total estim√© : <b>
                    {new Intl.NumberFormat('fr-MA', { style: 'currency', currency: 'MAD' })
                      .format(total)}
                  </b>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      )}
    </div>
  );
}

export default ProductDetailsPage;
