import React from "react";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { toggleWishlist } from "../actions/wishlistActions";
import { useCart } from "../cart/CartProvider";
import "./HomeProducts.css";

export default function HomeProductCard({ product }) {
  const id = product.id ?? product._id;
  const dispatch = useDispatch();
  const cart = useCart();

  const addToCart = () =>
    cart.addItem(
      {
        id,
        name: product.name,
        price: Number(product.price),
        image: product.image,
      },
      1
    );

  const addToWishlist = (e) => {
    e.preventDefault();
    dispatch(toggleWishlist(id));
  };

  const fmtMAD = (v) =>
    new Intl.NumberFormat("fr-MA", { style: "currency", currency: "MAD" }).format(Number(v || 0));

  return (
    <article className="hp-card">
      {!product.stock && <span className="hp-badge hp-badge--ko">Out of stock</span>}
      {product.has_discount && (
        <span className="hp-badge hp-badge--sale">-{product.discount_percent}%</span>
      )}

      <Link to={`/product/${id}/`} className="hp-media" aria-label={product.name}>
        {/* eslint-disable-next-line jsx-a11y/alt-text */}
        <img src={product.image} />
      </Link>

      {/* Hover actions */}
      <div className="hp-actions-row">
        <button type="button" className="hp-action-square" title="Wishlist" onClick={addToWishlist}>
          <i className="fas fa-heart" />
        </button>
        <Link className="hp-action-square" title="Voir" to={`/product/${id}/`}>
          <i className="fas fa-eye" />
        </Link>
      </div>

      <button type="button" className="hp-addbar" onClick={addToCart}>
        <i className="fas fa-shopping-bag mr-2" />
        AJOUTER AU PANIER
      </button>

      <div className="hp-body">
        {/* product name = normal weight */}
        <Link to={`/product/${id}/`} className="hp-title">
          {product.name}
        </Link>

        {/* price block */}
        {product.has_discount ? (
          <div className="hp-price-row">
            <span className="hp-price-old">{fmtMAD(product.old_price)}</span>
            <span className="hp-price-new">{fmtMAD(product.price)}</span>
          </div>
        ) : (
          <div className="hp-price-only">{fmtMAD(product.price)}</div>
        )}
      </div>
    </article>
  );
}
