// src/components/NavBar.js
import React from 'react';
import { useSelector } from 'react-redux';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import './navbar.css';

function NavBar() {
  const { userInfo } = useSelector((state) => state.userLoginReducer) || {};

  return (
    <Navbar
      expand="lg"
      className="navstrip custom-navbar"
      style={{
        background: 'var(--navbar-bg)',
        color: 'var(--navbar-text)',
      }}
    >
      <Container>
        <Navbar.Toggle aria-controls="main-nav" />
        <Navbar.Collapse id="main-nav">
          <Nav className="mx-auto align-items-lg-center">
            <LinkContainer to="/">
              <Nav.Link className="nav-link-upp">ACCEUIL</Nav.Link>
            </LinkContainer>

            <NavDropdown
              title={<span className="nav-link-upp">TOUS LES PRODUITS</span>}
              id="all-products"
            >
              <LinkContainer to="/products">
                <NavDropdown.Item>All</NavDropdown.Item>
              </LinkContainer>
              <NavDropdown.Divider />
              <LinkContainer to="/products?type=face"><NavDropdown.Item>Visage</NavDropdown.Item></LinkContainer>
              <LinkContainer to="/products?type=lips"><NavDropdown.Item>LÃ¨vres</NavDropdown.Item></LinkContainer>
              <LinkContainer to="/products?type=eyes"><NavDropdown.Item>Yeux</NavDropdown.Item></LinkContainer>
              <LinkContainer to="/products?type=eyebrow"><NavDropdown.Item>Sourcils</NavDropdown.Item></LinkContainer>
              <LinkContainer to="/products?type=hair"><NavDropdown.Item>Cheveux</NavDropdown.Item></LinkContainer>
            </NavDropdown>

            <LinkContainer to="/brands">
              <Nav.Link className="nav-link-upp">MARQUES</Nav.Link>
            </LinkContainer>

            <LinkContainer to="/contact">
              <Nav.Link className="nav-link-upp">CONTACT</Nav.Link>
            </LinkContainer>

            <LinkContainer to="/shipping">
              <Nav.Link className="nav-link-upp">TARIFS DE LIVRAISON</Nav.Link>
            </LinkContainer>

            {/* Use normalized admin flag */}
            {userInfo && userInfo.admin && (
              <LinkContainer to="/new-product/">
                <Nav.Link className="nav-link-upp ml-lg-3">
                  AJOUTER PRODUIT
                </Nav.Link>
              </LinkContainer>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
