// src/pages/ProductDetailsPage.js
import React, { useEffect, useMemo, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useHistory } from 'react-router-dom';
import './ProductDetailsPage.css';
import { Container, Row, Col, Button, Modal, Form, Spinner } from 'react-bootstrap';

import Message from '../components/Message';

// product actions
import { getProductDetails, deleteProduct } from '../actions/productActions';

// wishlist (single toggle endpoint)
import { toggleWishlist } from '../actions/wishlistActions';

// constants you already had
import {
  CREATE_PRODUCT_RESET,
  DELETE_PRODUCT_RESET,
  UPDATE_PRODUCT_RESET,
  CARD_CREATE_RESET,
} from '../constants';

function ProductDetailsPage({ match }) {
  const history  = useHistory();
  const dispatch = useDispatch();

  // ===== store state
  const { loading, error, product } = useSelector((s) => s.productDetailsReducer);
  const { userInfo } = useSelector((s) => s.userLoginReducer);
  const { success: productDeletionSuccess } = useSelector((s) => s.deleteProductReducer);

  // wishlist state from store
  const { items: wishlistItems = [] } = useSelector((s) => s.wishlist || {});
  const pid = product?.id;
  const inWishlist = useMemo(
    () => (pid ? wishlistItems.some((w) => (w.id || w._id) === pid) : false),
    [wishlistItems, pid]
  );

  // ===== ui state
  const [showDelete, setShowDelete] = useState(false);

  // qty
  const [qty, setQty] = useState(1);
  const plus  = () => setQty((q) => Math.min(99, q + 1));
  const minus = () => setQty((q) => Math.max(1, q - 1));

  // “Commander maintenant” modal + simple form
  const [orderOpen, setOrderOpen] = useState(false);
  const [custName, setCustName] = useState('');
  const [custPhone, setCustPhone] = useState('');
  const [custAddress, setCustAddress] = useState('');

  // ===== effects
  useEffect(() => {
    dispatch(getProductDetails(match.params.id));
    dispatch({ type: UPDATE_PRODUCT_RESET });
    dispatch({ type: CREATE_PRODUCT_RESET });
    dispatch({ type: CARD_CREATE_RESET });
  }, [dispatch, match.params.id]);

  useEffect(() => {
    if (productDeletionSuccess) {
      alert('Product successfully deleted.');
      history.push('/');
      dispatch({ type: DELETE_PRODUCT_RESET });
    }
  }, [productDeletionSuccess, history, dispatch]);

  // ===== handlers
  const confirmDelete = () => setShowDelete(true);
  const doDelete = () => {
    dispatch(deleteProduct(match.params.id));
    setShowDelete(false);
  };

  const handleToggleWishlist = () => {
    if (!pid) return;
    dispatch(toggleWishlist(pid));
  };

  // Open the order form modal
  const openOrder = () => setOrderOpen(true);

  // Send to WhatsApp after small validation
  const sendOrder = () => {
    if (!custName.trim() || !custPhone.trim() || !custAddress.trim()) {
      alert('Merci de remplir nom, téléphone et adresse.');
      return;
    }
    const price = Number(product?.price || 0);
    const total = price * qty;
    const adminPhone = '212612345678'; // <-- put your WhatsApp number (no +)

    const msg = [
      `*Nouvelle commande*`,
      `Produit : ${product?.name || '-'}`,
      `Quantité : ${qty}`,
      `Total estimé : ${new Intl.NumberFormat('fr-MA', { style: 'currency', currency: 'MAD' }).format(total)}`,
      `---`,
      `Nom : ${custName}`,
      `Téléphone : ${custPhone}`,
      `Adresse : ${custAddress}`,
    ].join('\n');

    const url = `https://wa.me/${adminPhone}?text=${encodeURIComponent(msg)}`;
    window.open(url, '_blank');
    setOrderOpen(false);
  };

  if (loading) {
    return (
      <div className="d-flex align-items-center">
        <h5 className="mb-0">Getting Product Details</h5>
        <Spinner animation="border" className="ml-2" />
      </div>
    );
  }

  return (
    <div>
      {error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Container className="pd">
          <Row className="gy-4">
            {/* LEFT – Sticky media */}
            <Col lg={6}>
              <div className="pd-media">
                <div className="pd-media-frame">
                  <img src={product?.image} alt={product?.name} />
                </div>
              </div>
            </Col>

            {/* RIGHT – Info / actions */}
            <Col lg={6}>
              <div className="pd-card mb-3">
                <h2 className="pd-title">{product?.name}</h2>

                <div className="pd-subtle mb-3">
                  {product?.category ? <>Catégorie : <b>{product.category}</b></> : null}
                </div>

                {/* status chip */}
                <div className="mb-3">
                  {product?.stock ? (
                    <span className="pd-chip pd-chip--ok">
                      <i className="fas fa-check-circle" /> En stock
                    </span>
                  ) : (
                    <span className="pd-chip pd-chip--ko">
                      <i className="fas fa-times-circle" /> Out of stock
                    </span>
                  )}
                </div>

                {/* description */}
                {product?.description && (
                  <p className="pd-subtle" style={{ lineHeight: 1.7 }}>
                    {product.description}
                  </p>
                )}

                {/* admin buttons */}
                {userInfo && userInfo.admin ? (
                  <div className="mt-3 d-flex" style={{ gap: 10 }}>
                    <Button variant="danger" onClick={confirmDelete}>
                      Delete Product
                    </Button>
                    <Button
                      variant="primary"
                      onClick={() => history.push(`/product-update/${product.id}/`)}
                    >
                      Edit Product
                    </Button>
                  </div>
                ) : null}
              </div>

              {/* price + qty + ctas */}
              <div className="pd-card">
                {/* price */}
                <div className="pd-price mb-3">
                  <span className="pd-subtle">Prix</span>
                  <strong>
                    {new Intl.NumberFormat('fr-MA', { style: 'currency', currency: 'MAD' })
                      .format(Number(product?.price || 0))}
                  </strong>
                </div>

                {/* quantity */}
                <div className="mb-3">
                  <div className="pd-subtle mb-2">Quantité</div>
                  <div className="pd-qty">
                    <button type="button" className="btn" onClick={minus} aria-label="minus">
                      &minus;
                    </button>
                    <input
                      value={qty}
                      onChange={(e) => {
                        const v = Number(e.target.value);
                        if (!Number.isNaN(v)) setQty(Math.max(1, Math.min(99, v)));
                      }}
                    />
                    <button type="button" className="btn" onClick={plus} aria-label="plus">
                      +
                    </button>
                  </div>
                </div>

                {/* actions */}
                <div className="pd-actions mb-2">
                  {/* WhatsApp order – always visible */}
                  <button className="pd-btn-primary" onClick={openOrder}>
                    <i className="fab fa-whatsapp mr-2" /> Commander maintenant
                  </button>

                  {/* Stripe checkout – only if in stock */}
                  {product?.stock ? (
                    <Link to={`${product.id}/checkout/`} className="pd-btn-outline btn">
                      Pay with Stripe
                    </Link>
                  ) : null}

                  {/* Wishlist toggle with filled heart */}
                  <button
                    onClick={handleToggleWishlist}
                    className="pd-btn-outline"
                    aria-label={inWishlist ? 'Retirer de la wishlist' : 'Ajouter à la wishlist'}
                    aria-pressed={inWishlist}
                    title={inWishlist ? 'Retirer de la wishlist' : 'Ajouter à la wishlist'}
                  >
                    <i className={inWishlist ? "fas fa-heart mr-2" : "far fa-heart mr-2"} />
                    {inWishlist ? 'Retirer de ma wishlist' : 'Ajouter à ma wishlist'}
                  </button>
                </div>

                {/* total */}
                <div className="pd-total">
                  Total estimé :{' '}
                  <b>
                    {new Intl.NumberFormat('fr-MA', { style: 'currency', currency: 'MAD' })
                      .format(Number(product?.price || 0) * qty)}
                  </b>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      )}

      {/* DELETE MODAL */}
      <Modal show={showDelete} onHide={() => setShowDelete(false)}>
        <Modal.Header closeButton>
          <Modal.Title>
            <i style={{ color: '#e6e600' }} className="fas fa-exclamation-triangle" /> Delete Confirmation
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete <em>"{product?.name}"</em>?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="danger" onClick={doDelete}>Confirm Delete</Button>
          <Button variant="primary" onClick={() => setShowDelete(false)}>Cancel</Button>
        </Modal.Footer>
      </Modal>

      {/* ORDER (WHATSAPP) MODAL */}
      <Modal show={orderOpen} onHide={() => setOrderOpen(false)}>
        <Modal.Header closeButton>
          <Modal.Title>
            <i className="fab fa-whatsapp mr-2" />
            Commander maintenant
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="orderName" className="mb-3">
              <Form.Label>Nom / Prénom</Form.Label>
              <Form.Control
                value={custName}
                onChange={(e) => setCustName(e.target.value)}
                placeholder="Votre nom"
              />
            </Form.Group>

            <Form.Group controlId="orderPhone" className="mb-3">
              <Form.Label>Téléphone (WhatsApp)</Form.Label>
              <Form.Control
                value={custPhone}
                onChange={(e) => setCustPhone(e.target.value)}
                placeholder="06 xx xx xx xx"
              />
            </Form.Group>

            <Form.Group controlId="orderAddress" className="mb-0">
              <Form.Label>Adresse</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={custAddress}
                onChange={(e) => setCustAddress(e.target.value)}
                placeholder="Ville, quartier, rue, n°..."
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="success" onClick={sendOrder}>Envoyer sur WhatsApp</Button>
          <Button variant="secondary" onClick={() => setOrderOpen(false)}>Annuler</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ProductDetailsPage;
