import React from "react";
import { Link } from "react-router-dom";
import { useCart } from "../cart/CartProvider";
import "./cart-page.css";

export default function CartPage() {
  const { items, totals, setQty, removeItem, clear } = useCart();

  if (!items.length) {
    return (
      <div className="cp-wrap">
        <h1>Votre panier</h1>
        <div className="cp-empty">Votre panier est vide.</div>
        <Link to="/products" className="cp-btn">Découvrir nos produits</Link>
      </div>
    );
  }

  return (
    <div className="cp-wrap">
      <h1>Votre panier</h1>

      <div className="cp-grid">
        <div className="cp-list">
          {items.map(it => (
            <div key={it.key} className="cp-row">
              <img src={it.image} alt={it.name} />
              <div className="cp-info">
                <div className="cp-title">{it.name}</div>
                {it.variantLabel && <div className="cp-variant">{it.variantLabel}</div>}
                <div className="cp-qty">
                  <button onClick={() => setQty(it.key, it.qty - 1)}>−</button>
                  <input value={it.qty} onChange={(e)=>setQty(it.key, Number(e.target.value)||1)} />
                  <button onClick={() => setQty(it.key, it.qty + 1)}>+</button>
                  <button className="cp-remove" onClick={() => removeItem(it.key)}>Supprimer</button>
                </div>
              </div>
              <div className="cp-price">{(it.price * it.qty).toFixed(2)} MAD</div>
            </div>
          ))}
          <button className="cp-clear" onClick={clear}>Vider le panier</button>
        </div>

        <aside className="cp-summary">
          <h3>Résumé</h3>
          <div className="cp-line">
            <span>Sous-total</span>
            <b>{totals.subtotal.toFixed(2)} MAD</b>
          </div>
          <div className="cp-note">Les frais de livraison sont calculés à l’étape suivante.</div>
          <Link to="/checkout" className="cp-btn primary">Passer à la caisse</Link>
        </aside>
      </div>
    </div>
  );
}
