// src/pages/ProductDetailsPage.js
import React, { useEffect, useMemo, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Spinner, Row, Col, Container, Button, Modal } from "react-bootstrap";
import Message from "../components/Message";
import { deleteProduct, getProductDetails } from "../actions/productActions";
import { toggleWishlist } from "../actions/wishlistActions";
import { useCart } from "../cart/CartProvider";
import {
  CREATE_PRODUCT_RESET,
  DELETE_PRODUCT_RESET,
  UPDATE_PRODUCT_RESET,
  CARD_CREATE_RESET,
} from "../constants";

function ProductDetailsPage({ history, match }) {
  const dispatch = useDispatch();
  const cart = useCart();

  // modal (delete)
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  // qty + variant
  const [qty, setQty] = useState(1);
  const [variantId, setVariantId] = useState(null);

  // data
  const { loading, error, product } = useSelector((s) => s.productDetailsReducer);
  const { userInfo } = useSelector((s) => s.userLoginReducer || {});
  const { success: productDeletionSuccess } = useSelector((s) => s.deleteProductReducer);
  const { items: wishlistItems = [] } = useSelector((s) => s.wishlist || {});
  const pid = product?.id;

  const inWishlist = pid
    ? wishlistItems.some((w) => (w.product?.id ?? w.id ?? w._id) === pid)
    : false;

  // variants
  const variants = useMemo(
    () => (Array.isArray(product?.variants) ? product.variants : []),
    [product?.variants]
  );
  const activeVariant = useMemo(
    () => variants.find((v) => String(v.id) === String(variantId)) || null,
    [variants, variantId]
  );

  // price to charge: variant if selected, else product price
  const unitPrice = activeVariant
    ? Number(activeVariant.price) || 0
    : Number(product?.price || 0);

  const total = unitPrice * qty;

  const fmtMAD = (v) =>
    new Intl.NumberFormat("fr-MA", { style: "currency", currency: "MAD" }).format(
      Number(v || 0)
    );

  // qty handlers
  const plus = () => setQty((q) => Math.min(99, q + 1));
  const minus = () => setQty((q) => Math.max(1, q - 1));

  // wishlist
  const handleToggleWishlist = () => {
    if (!pid) return;
    dispatch(toggleWishlist(pid));
  };

  // add to cart
  const addToCart = () => {
    if (!pid) return;
    cart.addItem(
      {
        id: pid,
        name: product?.name,
        price: unitPrice,
        image: product?.image,
        variantId: activeVariant ? activeVariant.id : null,
        variantLabel: activeVariant ? activeVariant.label : "",
      },
      qty
    );
    cart.setOpen(true);
  };

  // load product
  useEffect(() => {
    dispatch(getProductDetails(match.params.id));
    dispatch({ type: UPDATE_PRODUCT_RESET });
    dispatch({ type: CREATE_PRODUCT_RESET });
    dispatch({ type: CARD_CREATE_RESET });
  }, [dispatch, match.params.id]);

  // pick first in-stock variant on load
  useEffect(() => {
    if (variants.length && variantId == null) {
      const firstOk = variants.find((v) => v.in_stock) || variants[0];
      setVariantId(firstOk?.id ?? null);
    }
  }, [variants, variantId]);

  const confirmDelete = () => {
    dispatch(deleteProduct(match.params.id));
    handleClose();
  };

  if (productDeletionSuccess) {
    alert("Product successfully deleted.");
    history.push("/");
    dispatch({ type: DELETE_PRODUCT_RESET });
  }

  const showPromo = !!product?.has_discount; // always show if promo exists

  return (
    <div>
      {/* Delete modal */}
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>
            <i style={{ color: "#e6e600" }} className="fas fa-exclamation-triangle" /> Delete
            Confirmation
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete this product <em>"{product?.name}"</em>?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="danger" onClick={confirmDelete}>Confirm Delete</Button>
          <Button variant="primary" onClick={handleClose}>Cancel</Button>
        </Modal.Footer>
      </Modal>

      {loading && (
        <span style={{ display: "flex" }}>
          <h5>Getting Product Details</h5>
          <span className="ml-2"><Spinner animation="border" /></span>
        </span>
      )}

      {error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Container className="pd">
          <Row className="gy-4">
            {/* LEFT */}
            <Col lg={6}>
              <div className="pd-media">
                <div className="pd-media-frame">
                  <img src={product?.image} alt={product?.name} />
                  {showPromo && (
                    <span className="pd-sale-badge pd-sale-badge--media">
                      -{product.discount_percent}%
                    </span>
                  )}
                </div>
              </div>
            </Col>

            {/* RIGHT */}
            <Col lg={6}>
              <div className="pd-card mb-3">
                <h2 className="pd-title">{product?.name}</h2>

                <div className="pd-subtle mb-3">
                  {product?.category ? <>Cat√©gorie : <b>{product.category}</b></> : null}
                </div>

                <div className="mb-3">
                  {product?.stock ? (
                    <span className="pd-chip pd-chip--ok">
                      <i className="fas fa-check-circle" /> En stock
                    </span>
                  ) : (
                    <span className="pd-chip pd-chip--ko">
                      <i className="fas fa-times-circle" /> Out of stock
                    </span>
                  )}
                </div>

                {product?.description && (
                  <p className="pd-subtle" style={{ lineHeight: 1.7 }}>
                    {product.description}
                  </p>
                )}

                {/* Variants */}
                {variants.length > 0 && (
                  <div className="mb-3">
                    <div className="pd-subtle mb-2">Taille / Format</div>
                    <div className="pd-variants">
                      {variants.map((v) => (
                        <button
                          key={v.id}
                          type="button"
                          className={`pd-variant-pill${String(variantId) === String(v.id) ? " is-active" : ""}`}
                          disabled={!v.in_stock}
                          onClick={() => setVariantId(v.id)}
                          title={!v.in_stock ? "Indisponible" : v.label}
                          data-price={fmtMAD(v.price)}
                        >
                          {v.label}
                        </button>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              {/* Price + qty + actions */}
              <div className="pd-card">
                <div className="pd-price mb-3">
                  <span className="pd-subtle">Prix</span>
                  <div className="pd-price-wrap">
                    {showPromo && (
                      <>
                        <span className="pd-price-old">{fmtMAD(product.old_price)}</span>
                        <span className="pd-sale-badge">-{product.discount_percent}%</span>
                      </>
                    )}
                    <strong className="pd-price-new">{fmtMAD(unitPrice)}</strong>
                  </div>
                  {activeVariant && (
                    <div className="pd-subtle small mt-1">Variante : {activeVariant.label}</div>
                  )}
                </div>

                <div className="mb-3">
                  <div className="pd-subtle mb-2">Quantit√©</div>
                  <div className="pd-qty">
                    <button type="button" className="btn" onClick={minus} aria-label="minus">&minus;</button>
                    <input
                      value={qty}
                      onChange={(e) => {
                        const v = Number(e.target.value);
                        if (!Number.isNaN(v)) setQty(Math.max(1, Math.min(99, v)));
                      }}
                    />
                    <button type="button" className="btn" onClick={plus} aria-label="plus">+</button>
                  </div>
                </div>

                <div className="pd-actions mb-2">
                  <button className="pd-btn-primary" onClick={addToCart} disabled={!product?.stock}>
                    <i className="fas fa-shopping-bag mr-2" /> Ajouter au panier
                  </button>

                  <button onClick={handleToggleWishlist} className="pd-btn-outline">
                    {inWishlist ? "üíî Retirer de ma wishlist" : "ü§ç Ajouter √† ma wishlist"}
                  </button>

                  {userInfo && userInfo.admin && (
                    <>
                      <button className="pd-btn-outline" onClick={() => history.push(`/product-update/${product.id}/`)}>Edit Product</button>
                      <button className="pd-btn-outline" onClick={handleShow}>Delete Product</button>
                    </>
                  )}
                </div>

                <div className="pd-total">Total estim√© : <b>{fmtMAD(total)}</b></div>
              </div>
            </Col>
          </Row>
        </Container>
      )}
    </div>
  );
}

export default ProductDetailsPage;
