// src/components/BrandMarquee.js
import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";
import "./brand-marquee.css";

const LOGOS = {
  "beauty of joseon": "/brands/boj.svg",
  "axis-y": "/brands/axisy.svg",
  "cosrx": "/brands/cosrx.svg",
  "skin1004": "/brands/skin1004.svg",
  "dr. althea": "/brands/dralthea.svg",
  "i'm from": "/brands/imfrom.svg",
  "anua": "/brands/anua.svg",
};

// One scrolling track; duplicate it for a seamless loop
function Track({ brands, hidden = false }) {
  return (
    <div className={`bm-track${hidden ? " bm-track--2" : ""}`} aria-hidden={hidden || undefined}>
      {brands.map((name, i) => {
        const label = String(name || "").trim();
        const key = label.toLowerCase();
        const src = LOGOS[key];
        return (
          <div className="bm-card" key={`${key}-${i}`}>
            {src ? <img src={src} alt={label} /> : <span className="bm-text">{label}</span>}
          </div>
        );
      })}
    </div>
  );
}

export default function BrandMarquee() {
  const [brands, setBrands] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let alive = true;
    (async () => {
      try {
        const { data } = await axios.get("/api/brands/");
        if (alive) setBrands(Array.isArray(data) ? data.filter(Boolean) : []);
      } finally {
        if (alive) setLoading(false);
      }
    })();
    return () => { alive = false; };
  }, []);

  // Duplicate the list so a single track is at least 200% width (smooth loop)
  const display = useMemo(() => {
    if (!brands.length) return [];
    return [...brands, ...brands];
  }, [brands]);

  if (loading || !display.length) return null;

  return (
    <section className="bm-wrap">
      <h2 className="bm-title">Toutes nos marques</h2>
      <div className="bm-rail">
        {/* first track */}
        <Track brands={display} />
        {/* second track, slightly offset (CSS provides delay via .bm-track--2 if you want) */}
        <Track brands={display} hidden />
      </div>
    </section>
  );
}
