import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchWishlist, removeFromWishlist } from "../actions/wishlistActions";
import { Link, useHistory } from "react-router-dom";

const WishlistPage = () => {
  const dispatch = useDispatch();
  const history = useHistory();
  const { items, loading, error } = useSelector((s) => s.wishlist);
  const { userInfo } = useSelector((s) => s.userLogin || {});

  useEffect(() => {
    if (!userInfo) {
      history.push("/login?redirect=/wishlist");
    } else {
      dispatch(fetchWishlist());
    }
  }, [dispatch, userInfo, history]);

  if (loading) return <div style={{ padding: 16 }}>Chargementâ€¦</div>;
  if (error) return <div style={{ padding: 16, color: "red" }}>{error}</div>;

  if (!items?.length) {
    return (
      <div style={{ padding: 16 }}>
        <h2>Ma wishlist</h2>
        <p>Ta wishlist est vide.</p>
        <Link to="/products">Voir les produits</Link>
      </div>
    );
  }

  return (
    <div style={{ padding: 16 }}>
      <h2>Ma wishlist ({items.length})</h2>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(220px, 1fr))",
          gap: 16,
        }}
      >
        {items.map((p) => (
          <div key={p._id} style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
            <Link to={`/product/${p._id}`} style={{ textDecoration: "none", color: "inherit" }}>
              <img
                src={p.image}
                alt={p.name}
                style={{ width: "100%", height: 160, objectFit: "cover", borderRadius: 8 }}
              />
              <h4 style={{ margin: "8px 0 4px" }}>{p.name}</h4>
              {typeof p.price !== "undefined" && (
                <div style={{ fontWeight: 600 }}>{Number(p.price).toFixed(2)} MAD</div>
              )}
              {p.inStock === false && (
                <div style={{ fontSize: 12, color: "#b00" }}>Indisponible</div>
              )}
            </Link>
            <button
              onClick={() => dispatch(removeFromWishlist(p._id))}
              style={{
                marginTop: 8,
                width: "100%",
                padding: "8px 12px",
                borderRadius: 10,
                border: "1px solid #ddd",
                cursor: "pointer",
              }}
            >
              Retirer
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WishlistPage;
