import React, { useState } from 'react';
import { Container, Dropdown, Form, InputGroup, Button } from 'react-bootstrap';
import { Link, useHistory } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { logout } from '../actions/userActions';

function BrandSearch() {
  const [open, setOpen] = useState(false);
  const [q, setQ] = useState('');
  const history = useHistory();

  const submit = (e) => {
    e.preventDefault();
    if (!q.trim()) return;
    history.push(`/products?search=${encodeURIComponent(q.trim().toLowerCase())}`);
    setOpen(false);
  };

  return (
    <div className="brand-search">
      {!open ? (
        <Button
          variant="link"
          className="icon-link p-0"
          onClick={() => setOpen(true)}
          aria-label="Open search"
        >
          <i className="fas fa-search" />
        </Button>
      ) : (
        <Form onSubmit={submit} className="brand-search-form">
          <InputGroup size="sm">
            <Form.Control
              autoFocus
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="search products"
              className="brand-search-input"
            />
            <Button type="submit" variant="dark" className="brand-search-btn">
              <i className="fas fa-search" />
            </Button>
            <Button
              variant="link"
              className="brand-search-close"
              onClick={() => setOpen(false)}
              aria-label="Close search"
            >
              <i className="fas fa-times" />
            </Button>
          </InputGroup>
        </Form>
      )}
    </div>
  );
}

export default function BrandBar() {
  const dispatch = useDispatch();
  const history = useHistory();

  const { userInfo } = useSelector((state) => state.userLoginReducer) || {};
  // ➕ compteur wishlist
  const { items: wishlistItems = [] } = useSelector((s) => s.wishlist || {});
  const wishCount = wishlistItems.length || 0;

  const logoutHandler = () => {
    dispatch(logout());
    history.push('/login');
    window.location.reload();
  };

  return (
    <div className="brandbar">
      <Container className="brandbar-inner">
        {/* Left side (placeholder) */}
        <div className="brandbar-left" />

        {/* Centered logo */}
        <div className="brandbar-logo">
          <Link to="/">
            <img src="/brand/logob.svg" alt="Beauty Shop" className="brand-logo-img" />
          </Link>
        </div>

        {/* Right side icons + user menu + search */}
        <div className="brandbar-icons">
          {/* Search (inline pop) */}
          <BrandSearch />

          {/* Wishlist with badge */}
          <Link to="/wishlist" title="Wishlist" className="icon-link" style={{ position: 'relative' }}>
            <i className="fas fa-heart" />
            {wishCount > 0 && (
              <span
                style={{
                  position: 'absolute',
                  top: -6,
                  right: -10,
                  background: '#ff3b3b',
                  color: '#fff',
                  borderRadius: 999,
                  padding: '2px 6px',
                  fontSize: 11,
                  lineHeight: 1,
                  fontWeight: 700,
                }}
              >
                {wishCount}
              </span>
            )}
          </Link>

          {/* Tu as dit ne plus utiliser le panier — on peut masquer ce lien */}
          {/* <Link to="/cart" title="Cart" className="icon-link">
            <i className="fas fa-shopping-bag" />
          </Link> */}

          {/* User dropdown (déplacé ici depuis Navbar) */}
          {userInfo ? (
            <Dropdown align="end">
              <Dropdown.Toggle
                as="button"
                className="icon-link btn btn-link p-0 brand-user-toggle"
                aria-label="User menu"
              >
                <i className="fas fa-user" />
              </Dropdown.Toggle>

              <Dropdown.Menu className="brand-user-menu">
                <Dropdown.Header className="text-capitalize">{userInfo.username}</Dropdown.Header>

                <Dropdown.Item as={Link} to="/account">
                  Account Settings
                </Dropdown.Item>
                <Dropdown.Item as={Link} to="/all-orders/">
                  All Orders
                </Dropdown.Item>

                {userInfo.admin && (
                  <>
                    <Dropdown.Divider />
                    <Dropdown.Item as={Link} to="/new-product/">
                      Add Product
                    </Dropdown.Item>
                  </>
                )}

                <Dropdown.Divider />
                <Dropdown.Item onClick={logoutHandler}>Logout</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          ) : (
            <Link to="/login" title="Login" className="icon-link">
              <i className="fas fa-user" />
            </Link>
          )}
        </div>
      </Container>
    </div>
  );
}
