// src/components/BrandMarquee.jsx
import React from "react";
import "./brand-marquee.css";

/**
 * Add your brand logo PNG/SVGs to /public/brands/...
 * You can mix real logos and text fallbacks.
 */
const BRANDS = [
  { name: "Beauty of Joseon", src: "/brands/boj.svg" },
  { name: "AXIS-Y",           src: "/brands/axisy.svg" },
  { name: "COSRX",            src: "/brands/cosrx.svg" },
  { name: "SKIN1004",         src: "/brands/skin1004.svg" },
  { name: "Dr. Althea",       src: "/brands/dralthea.svg" },
  { name: "I'm From",         src: "/brands/imfrom.svg" },
  { name: "ANUA",             src: "/brands/anua.svg" },
];

export default function BrandMarquee() {
  // duplicate list → seamless loop
  const list = [...BRANDS, ...BRANDS];

  return (
    <section className="bm-wrap">
      <h2 className="bm-title">Toutes nos marques</h2>

      <div className="bm-rail">
        <div className="bm-track" aria-hidden="true">
          {list.map((b, i) => (
            <div className="bm-card" key={`${b.name}-${i}`}>
              {b.src ? (
                <img src={b.src} alt={b.name} />
              ) : (
                <span className="bm-text">{b.name}</span>
              )}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
