// src/pages/LoginPage.jsx
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { Form, Button } from "react-bootstrap";
import { login } from "../actions/userActions";
import "./login-register.css";

export default function LoginPage() {
  const history = useHistory();
  const [email, setEmail] = useState("");            // ‚úÖ email now
  const [password, setPassword] = useState("");

  const dispatch = useDispatch();
  const { error, userInfo } = useSelector((s) => s.userLoginReducer || {});

  useEffect(() => {
    if (userInfo) history.push("/");
  }, [userInfo, history]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(login(email, password));               // ‚úÖ send email
  };

  return (
    <div className="auth-page">
      <div className="auth-card">
        <div className="auth-side">
          <div className="auth-brand">
            <img src="/brand/logop.png" alt="MiniGlow" />
          </div>
          <h2 className="auth-headline">Ravivez votre √©clat ‚ú®</h2>
          <p className="auth-blurb">Connectez-vous pour poursuivre vos coups de c≈ìur.</p>
          <ul className="auth-bullets">
            <li>Suivi de commandes</li>
            <li>Wishlist synchronis√©e</li>
            <li>Offres personnalis√©es</li>
          </ul>
        </div>

        <div className="auth-form">
          <h1 className="auth-title">Connexion</h1>
          <p className="auth-sub">Ravi de vous revoir üëã</p>

          {error && <div className="alert danger">{error}</div>}

          <Form onSubmit={submitHandler} className="auth-form-inner">
            <Form.Group controlId="email" className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="Entrez votre email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="password" className="mb-4">
              <Form.Label>Mot de passe</Form.Label>
              <Form.Control
                type="password"
                placeholder="Entrez votre mot de passe"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>

            <Button type="submit" variant="dark" className="auth-btn-primary">
              Se connecter
            </Button>
          </Form>

          <div className="auth-switch">
            <Link to="/forgot-password" className="auth-link">Mot de passe oubli√© ?</Link>
          </div>

          <div className="auth-switch">
            Pas de compte ? <Link to="/register">Cr√©er un compte</Link>
          </div>
        </div>
      </div>
    </div>
  );
}
