// src/pages/WishlistPage.js
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { fetchWishlist, toggleWishlist } from "../actions/wishlistActions";
import Message from "../components/Message";
import { Container, Row, Col, Card, Button, Spinner } from "react-bootstrap";

export default function WishlistPage() {
  const dispatch = useDispatch();
  const { items = [], loading, error } = useSelector((s) => s.wishlist || {});

  useEffect(() => {
    dispatch(fetchWishlist());
  }, [dispatch]);

  if (loading) {
    return (
      <div className="d-flex align-items-center">
        <Spinner animation="border" className="mr-2" />
        Loading wishlist…
      </div>
    );
  }

  if (error) return <Message variant="danger">{error}</Message>;

  return (
    <Container className="py-4">
      <h3 className="mb-4">My Wishlist</h3>
      {items.length === 0 ? (
        <Message variant="info">
          Your wishlist is empty. <Link to="/products">Browse products</Link>
        </Message>
      ) : (
        <Row>
          {items.map((p) => (
            <Col key={p.id || p._id} xs={12} sm={6} md={4} lg={3} className="mb-4">
              <Card className="h-100">
                {p.image && (
                  <Card.Img
                    variant="top"
                    src={p.image}
                    style={{ objectFit: "cover", height: 220 }}
                  />
                )}
                <Card.Body className="d-flex flex-column">
                  <Card.Title className="mb-2">{p.name}</Card.Title>
                  <Card.Text className="text-muted mb-3" style={{ minHeight: 48 }}>
                    {p.description?.slice(0, 60)}
                    {p.description?.length > 60 ? "…" : ""}
                  </Card.Text>
                  <div className="mt-auto d-flex justify-content-between align-items-center">
                    <strong>
                      {new Intl.NumberFormat("fr-MA", {
                        style: "currency",
                        currency: "MAD",
                      }).format(Number(p.price || 0))}
                    </strong>
                    <div>
                      <Link to={`/product/${p.id || p._id}/`} className="btn btn-sm btn-outline-primary mr-2">
                        View
                      </Link>
                      <Button
                        size="sm"
                        variant="outline-danger"
                        onClick={() => dispatch(toggleWishlist(p.id || p._id))}
                      >
                        Remove
                      </Button>
                    </div>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}
