// src/pages/ProductUpdatePage.js
import React, { useEffect, useState } from 'react';
import { Form, Button, Spinner, InputGroup } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { getProductDetails, updateProduct } from '../actions/productActions';
import { checkTokenValidation, logout } from '../actions/userActions';
import { UPDATE_PRODUCT_RESET } from '../constants';
import Message from '../components/Message';

export default function ProductUpdatePage({ match }) {
  const productId = match.params.id;
  const history = useHistory();
  const dispatch = useDispatch();

  // Base fields
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('other');

  // Promo fields
  const [price, setPrice] = useState('');       // current selling price
  const [oldPrice, setOldPrice] = useState(''); // anchor (strike)
  const [discountPct, setDiscountPct] = useState(''); // auto

  const [stock, setStock] = useState(false);

  // Image
  const [newImage, setNewImage] = useState(false);
  const [image, setImage] = useState(null);

  // Variants
  const [variants, setVariants] = useState([]);
  const addVariantRow = () => setVariants(v => [...v, { label: '', size_ml: '', price: '', in_stock: true, sku: '' }]);
  const removeVariantRow = (idx) => setVariants(v => v.filter((_, i) => i !== idx));
  const changeVariant = (idx, field, value) =>
    setVariants(v => v.map((row, i) => (i === idx ? { ...row, [field]: value } : row)));

  // Reducers
  const { userInfo } = useSelector(s => s.userLoginReducer || {});
  const { product, loading: loadingDetails } = useSelector(s => s.productDetailsReducer || {});
  const { success, loading: loadingUpdate, error } = useSelector(s => s.updateProductReducer || {});
  const { error: tokenError } = useSelector(s => s.checkTokenValidationReducer || {});

  // Load product
  useEffect(() => {
    if (!userInfo || !userInfo.admin) { history.push('/login'); return; }
    dispatch(checkTokenValidation());
    dispatch(getProductDetails(productId));
  }, [dispatch, userInfo, history, productId]);

  // Hydrate form
  useEffect(() => {
    if (!product || !product.id) return;
    setName(product.name || '');
    setDescription(product.description || '');
    setBrand(product.brand || '');
    setCategory(product.category || 'other');
    setPrice(product.price != null ? String(product.price) : '');
    setOldPrice(product.old_price != null ? String(product.old_price) : '');
    setStock(!!product.stock);
    setVariants(
      (product.variants || []).map(v => ({
        label: v.label || '',
        size_ml: v.size_ml ?? '',
        price: v.price != null ? String(v.price) : '',
        in_stock: !!v.in_stock,
        sku: v.sku || '',
      }))
    );
  }, [product]);

  // ---------- AUTO-CALC LOGIC ----------
  // whenever oldPrice or price changes → recompute % (if both valid)
  useEffect(() => {
    const op = parseFloat(oldPrice);
    const np = parseFloat(price);
    if (isFinite(op) && op > 0 && isFinite(np) && np >= 0) {
      const pct = Math.max(0, Math.round(((op - np) / op) * 100));
      setDiscountPct(String(pct));
    } else {
      setDiscountPct('');
    }
  }, [oldPrice, price]);

  // when user edits % → recompute price from oldPrice
  const onChangePct = (v) => {
    setDiscountPct(v);
    const op = parseFloat(oldPrice);
    const pct = parseFloat(v);
    if (isFinite(op) && op > 0 && isFinite(pct) && pct >= 0 && pct < 100) {
      const np = +(op * (100 - pct) / 100).toFixed(2);
      setPrice(String(np));
    }
  };
  // -------------------------------------

  const onSubmit = (e) => {
    e.preventDefault();
    const fd = new FormData();
    fd.append('name', name);
    fd.append('description', description);
    fd.append('price', price);                 // current price
    if (oldPrice !== '') fd.append('old_price', oldPrice); // optional
    fd.append('stock', stock);
    fd.append('brand', brand);
    fd.append('category', category);
    if (newImage && image) fd.append('image', image);

    const clean = variants
      .filter(v => v.label && String(v.price) !== '')
      .map(v => ({
        label: v.label,
        size_ml: v.size_ml === '' ? null : Number(v.size_ml),
        price: Number(v.price),
        in_stock: !!v.in_stock,
        sku: v.sku || ''
      }));
    if (clean.length) fd.append('variants', JSON.stringify(clean));

    dispatch(updateProduct(productId, fd));
  };

  if (success) {
    alert('Product successfully updated.');
    dispatch({ type: UPDATE_PRODUCT_RESET });
    history.push(`/product/${productId}/`);
  }

  if (userInfo && tokenError === 'Request failed with status code 401') {
    alert('Session expired, please login again.');
    dispatch(logout());
    history.push('/login');
    window.location.reload();
  }

  return (
    <div>
      <span className="d-flex justify-content-center text-info">
        <em>Edit Product</em>
      </span>

      {error && (
        <Message variant="danger">
          {error.image ? error.image[0] : String(error)}
        </Message>
      )}

      {loadingDetails && (
        <span style={{ display: 'flex' }}>
          <h5>Getting Product Details</h5>
          <span className="ml-2"><Spinner animation="border" /></span>
        </span>
      )}

      {loadingUpdate && (
        <span style={{ display: 'flex' }}>
          <h5>Updating Product</h5>
          <span className="ml-2"><Spinner animation="border" /></span>
        </span>
      )}

      {product && (
        <Form onSubmit={onSubmit}>
          {/* Image */}
          <Form.Group controlId="image">
            <Form.Label><b>Product Image</b></Form.Label>
            <p>{product.image && <img src={product.image} alt={product.name} height="200" />}</p>
            {newImage ? (
              <>
                <Form.Control type="file" onChange={(e) => setImage(e.target.files[0])} />
                <span onClick={() => { setNewImage(false); setImage(null); }} className="btn btn-primary btn-sm mt-2">Cancel</span>
              </>
            ) : (
              <p><span onClick={() => setNewImage(true)} className="btn btn-success btn-sm">choose different image</span></p>
            )}
          </Form.Group>

          {/* Basics */}
          <Form.Group controlId="name">
            <Form.Label><b>Product Name</b></Form.Label>
            <Form.Control value={name} onChange={(e) => setName(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="description">
            <Form.Label><b>Product Description</b></Form.Label>
            <Form.Control value={description} onChange={(e) => setDescription(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="brand">
            <Form.Label><b>Brand (Marque)</b></Form.Label>
            <Form.Control value={brand} onChange={(e) => setBrand(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="category">
            <Form.Label><b>Category</b></Form.Label>
            <Form.Control as="select" value={category} onChange={(e) => setCategory(e.target.value)}>
              <option value="face">Face</option>
              <option value="lips">Lips</option>
              <option value="eyes">Eyes</option>
              <option value="eyebrow">Eyebrow</option>
              <option value="hair">Hair</option>
              <option value="other">Other</option>
            </Form.Control>
          </Form.Group>

          {/* Promo section */}
          <Form.Group controlId="old_price">
            <Form.Label><b>Old price (optional)</b></Form.Label>
            <Form.Control
              type="number" step="0.01"
              value={oldPrice} placeholder="e.g. 199.00"
              onChange={(e) => setOldPrice(e.target.value)}
            />
            <small className="text-muted">If set and greater than Price, a discount badge is shown.</small>
          </Form.Group>

          <Form.Group controlId="discountPct">
            <Form.Label><b>Discount %</b></Form.Label>
            <InputGroup>
              <Form.Control
                type="number" step="1" min="0" max="95"
                value={discountPct}
                onChange={(e) => onChangePct(e.target.value)}
                placeholder="auto"
              />
              <InputGroup.Append>
                <InputGroup.Text>%</InputGroup.Text>
              </InputGroup.Append>
            </InputGroup>
            <small className="text-muted">Editing % recalculates the selling price from the Old price.</small>
          </Form.Group>

          <Form.Group controlId="price">
            <Form.Label><b>Price (current selling price)</b></Form.Label>
            <Form.Control
              type="number" step="0.01"
              value={price} placeholder="e.g. 155.00"
              onChange={(e) => setPrice(e.target.value)}
            />
          </Form.Group>

          {/* Stock */}
          <div className="d-flex align-items-center mb-3">
            <Form.Label className="mb-0">In Stock</Form.Label>
            <Form.Check className="ml-2" type="checkbox" checked={stock} onChange={() => setStock(!stock)} />
          </div>

          {/* Variants */}
          <Form.Group>
            <Form.Label><b>Sizes / Variants</b></Form.Label>
            {variants.map((row, i) => (
              <div key={i} className="d-flex align-items-center mb-2" style={{ gap: 8 }}>
                <Form.Control style={{ maxWidth: 240 }} placeholder="Label (e.g. 500 ml)" value={row.label} onChange={(e) => changeVariant(i, 'label', e.target.value)} />
                <Form.Control type="number" style={{ maxWidth: 120 }} placeholder="Size ml (opt.)" value={row.size_ml} onChange={(e) => changeVariant(i, 'size_ml', e.target.value)} />
                <Form.Control type="number" step="0.01" style={{ maxWidth: 140 }} placeholder="Price" value={row.price} onChange={(e) => changeVariant(i, 'price', e.target.value)} />
                <Form.Check className="ml-2" label="In stock" checked={row.in_stock} onChange={(e) => changeVariant(i, 'in_stock', e.target.checked)} />
                <Form.Control style={{ maxWidth: 140 }} placeholder="SKU (opt.)" value={row.sku} onChange={(e) => changeVariant(i, 'sku', e.target.value)} />
                <Button variant="outline-danger" size="sm" className="ml-2" onClick={() => removeVariantRow(i)}>Remove</Button>
              </div>
            ))}
            <Button variant="outline-secondary" size="sm" className="mt-2" onClick={addVariantRow}>+ Add size</Button>
          </Form.Group>

          <Button type="submit" variant="success" className="btn-sm mb-4">Save Changes</Button>
          <Button onClick={() => history.push(`/product/${productId}/`)} variant="primary" className="btn-sm ml-2 mb-4">Cancel</Button>
        </Form>
      )}
    </div>
  );
}
