import React from "react";
import { Link } from "react-router-dom";
import "./HomeProducts.css"; // styles below

export default function HomeProductCard({ product }) {
  const priceMAD = new Intl.NumberFormat("fr-MA", {
    style: "currency",
    currency: "MAD",
  }).format(Number(product?.price || 0));

  return (
    <article className="hp-card">
      {/* Optional corner badge */}
      {!product?.stock ? (
        <span className="hp-badge hp-badge--ko">Out of stock</span>
      ) : product?.isNew ? (
        <span className="hp-badge hp-badge--new">New</span>
      ) : null}

      {/* Media */}
      <Link to={`/product/${product.id}/`} className="hp-media">
        <img src={product.image} alt={product.name} loading="lazy" />
      </Link>

      {/* Hover actions (optional) */}
      <div className="hp-actions">
        <Link to={`/product/${product.id}/`} className="hp-action">
          <i className="fas fa-eye" />
        </Link>
        <Link to={`/product/${product.id}/`} className="hp-action">
          <i className="fas fa-shopping-bag" />
        </Link>
        {/* if you wired wishlist on the header, you could add another action here */}
      </div>

      {/* Content */}
      <div className="hp-body">
        <Link to={`/product/${product.id}/`} className="hp-title">
          {product.name}
        </Link>
        <div className="hp-price">{priceMAD}</div>
      </div>
    </article>
  );
}
