import React, { useState } from "react";
import axios from "axios";
import "./contact.css";

export default function ContactPage() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [message, setMessage] = useState("");

  const [sending, setSending] = useState(false);
  const [ok, setOk] = useState(false);
  const [err, setErr] = useState("");

  const submit = async (e) => {
    e.preventDefault();
    setErr("");
    setOk(false);

    if (!name.trim() || !email.trim() || !message.trim()) {
      setErr("Merci de renseigner votre nom, e-mail et message.");
      return;
    }
    setSending(true);
    try {
      await axios.post("/api/contact/", {
        name: name.trim(),
        email: email.trim(),
        phone: phone.trim(),
        message: message.trim(),
      });
      setOk(true);
      setName("");
      setEmail("");
      setPhone("");
      setMessage("");
    } catch (e) {
      setErr(e?.response?.data?.detail || e.message || "Erreur d’envoi.");
    } finally {
      setSending(false);
    }
  };

  return (
    <div className="ct-wrap">
      <section className="ct-hero">
        <h1 className="ct-title">Contacter nous</h1>

        <div className="ct-infos">
          <p><b>Adresse :</b> Casablanca, Maroc</p>
          <p><b>WhatsApp :</b> +212 6 12 34 56 78</p>
          <p><b>E-mail :</b> contact@votresite.com</p>
          <p className="ct-note">
            Nous sommes là pour vous aider ! Pour toute question ou assistance, contactez-nous
            sur WhatsApp ou Instagram <i>pour une réponse rapide</i>.
          </p>
          <div className="ct-socials">
            <a href="https://facebook.com" target="_blank" rel="noreferrer" aria-label="Facebook"></a>
            <a href="https://instagram.com" target="_blank" rel="noreferrer" aria-label="Instagram"></a>
            <a href="https://wa.me/212612345678" target="_blank" rel="noreferrer" aria-label="WhatsApp"></a>
          </div>
        </div>
      </section>

      <section className="ct-form-card">
        <h2 className="ct-form-title">Restons en contact</h2>
        <p className="ct-form-sub">
          Nous sommes là pour vous aider ! Si vous avez des questions ou besoin d’assistance,
          n’hésitez pas à nous contacter via le formulaire ci-dessous.
        </p>

        <form onSubmit={submit} className="ct-form">
          <div className="ct-row">
            <input
              className="ct-input"
              placeholder="Nom *"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              className="ct-input"
              placeholder="E-mail *"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <input
            className="ct-input"
            placeholder="Téléphone (optionnel)"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />

          <textarea
            className="ct-textarea"
            placeholder="Message *"
            rows={8}
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />

          {err && <div className="ct-alert ct-alert-danger">{err}</div>}
          {ok && (
            <div className="ct-alert ct-alert-success">
              Merci ! Votre message a bien été envoyé. Nous revenons vers vous rapidement.
            </div>
          )}

          <button className="ct-submit" type="submit" disabled={sending}>
            {sending ? "Envoi…" : "Envoyer"}
          </button>
        </form>
      </section>
    </div>
  );
}
