// src/pages/ProductUpdatePage.js
import React, { useEffect, useState } from 'react';
import { Form, Button, Spinner } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { getProductDetails, updateProduct } from '../actions/productActions';
import { checkTokenValidation, logout } from '../actions/userActions';
import { UPDATE_PRODUCT_RESET } from '../constants';
import Message from '../components/Message';

export default function ProductUpdatePage({ match }) {
  const productId = match.params.id;
  const history = useHistory();
  const dispatch = useDispatch();

  // States locaux (contrôlés)
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('other');
  const [price, setPrice] = useState('');
  const [stock, setStock] = useState(false);

  // Image
  const [newImage, setNewImage] = useState(false);
  const [image, setImage] = useState(null);

  // Variantes
  const [variants, setVariants] = useState([]);

  const addVariantRow = () =>
    setVariants(v => [...v, { label: '', size_ml: '', price: '', in_stock: true, sku: '' }]);

  const removeVariantRow = (idx) =>
    setVariants(v => v.filter((_, i) => i !== idx));

  const changeVariant = (idx, field, value) =>
    setVariants(v => v.map((row, i) => (i === idx ? { ...row, [field]: value } : row)));

  // Reducers
  const { userInfo } = useSelector(s => s.userLoginReducer);
  const { product, loading: loadingDetails } = useSelector(s => s.productDetailsReducer);
  const { success, loading: loadingUpdate, error } = useSelector(s => s.updateProductReducer);
  const { error: tokenError } = useSelector(s => s.checkTokenValidationReducer);

  // Charger détails
  useEffect(() => {
    if (!userInfo || !userInfo.admin) { history.push('/login'); return; }
    dispatch(checkTokenValidation());
    dispatch(getProductDetails(productId));
  }, [dispatch, userInfo, history, productId]);

  // Hydrater les champs quand le produit arrive
  useEffect(() => {
    if (!product || !product.id) return;
    setName(product.name || '');
    setDescription(product.description || '');
    setBrand(product.brand || '');
    setCategory(product.category || 'other');
    setPrice(String(product.price ?? ''));
    setStock(!!product.stock);
    setVariants(
      (product.variants || []).map(v => ({
        label: v.label || '',
        size_ml: v.size_ml ?? '',
        price: String(v.price ?? ''),
        in_stock: !!v.in_stock,
        sku: v.sku || '',
      }))
    );
  }, [product]);

  const onSubmit = (e) => {
    e.preventDefault();

    const fd = new FormData();
    fd.append('name', name);
    fd.append('description', description);
    fd.append('price', price);
    fd.append('stock', stock);
    fd.append('brand', brand);
    fd.append('category', category);
    if (newImage && image) fd.append('image', image);

    const clean = variants
      .filter(v => v.label && String(v.price) !== '')
      .map(v => ({
        label: v.label,
        size_ml: v.size_ml === '' ? null : Number(v.size_ml),
        price: Number(v.price),
        in_stock: !!v.in_stock,
        sku: v.sku || ''
      }));
    if (clean.length) fd.append('variants', JSON.stringify(clean));

    dispatch(updateProduct(productId, fd));
  };

  if (success) {
    alert('Product successfully updated.');
    dispatch({ type: UPDATE_PRODUCT_RESET });
    history.push(`/product/${productId}/`);
  }

  if (userInfo && tokenError === 'Request failed with status code 401') {
    alert('Session expired, please login again.');
    dispatch(logout());
    history.push('/login');
    window.location.reload();
  }

  return (
    <div>
      <span className="d-flex justify-content-center text-info">
        <em>Edit Product</em>
      </span>

      {error && (
        <Message variant="danger">
          {error.image ? error.image[0] : String(error)}
        </Message>
      )}

      {loadingDetails && (
        <span style={{ display: 'flex' }}>
          <h5>Getting Product Details</h5>
          <span className="ml-2"><Spinner animation="border" /></span>
        </span>
      )}

      {loadingUpdate && (
        <span style={{ display: 'flex' }}>
          <h5>Updating Product</h5>
          <span className="ml-2"><Spinner animation="border" /></span>
        </span>
      )}

      {product && (
        <Form onSubmit={onSubmit}>
          {/* Image actuelle + remplacement */}
          <Form.Group controlId="image">
            <Form.Label><b>Product Image</b></Form.Label>
            <p>
              {product.image && <img src={product.image} alt={product.name} height="200" />}
            </p>

            {newImage ? (
              <>
                <Form.Control type="file" onChange={(e) => setImage(e.target.files[0])} />
                <span
                  onClick={() => { setNewImage(false); setImage(null); }}
                  className="btn btn-primary btn-sm mt-2"
                >
                  Cancel
                </span>
              </>
            ) : (
              <p>
                <span
                  onClick={() => setNewImage(true)}
                  className="btn btn-success btn-sm"
                >
                  choose different image
                </span>
              </p>
            )}
          </Form.Group>

          {/* Nom */}
          <Form.Group controlId="name">
            <Form.Label><b>Product Name</b></Form.Label>
            <Form.Control
              autoFocus value={name} placeholder="product name"
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>

          {/* Description */}
          <Form.Group controlId="description">
            <Form.Label><b>Product Description</b></Form.Label>
            <Form.Control
              value={description} placeholder="product description"
              onChange={(e) => setDescription(e.target.value)}
            />
          </Form.Group>

          {/* Marque */}
          <Form.Group controlId="brand">
            <Form.Label><b>Brand (Marque)</b></Form.Label>
            <Form.Control
              value={brand} placeholder="Ex. The Ordinary"
              onChange={(e) => setBrand(e.target.value)}
            />
          </Form.Group>

          {/* Catégorie */}
          <Form.Group controlId="category">
            <Form.Label><b>Category</b></Form.Label>
            <Form.Control as="select" value={category}
              onChange={(e) => setCategory(e.target.value)}>
              <option value="face">Face</option>
              <option value="lips">Lips</option>
              <option value="eyes">Eyes</option>
              <option value="eyebrow">Eyebrow</option>
              <option value="hair">Hair</option>
              <option value="other">Other</option>
            </Form.Control>
          </Form.Group>

          {/* Prix */}
          <Form.Group controlId="price">
            <Form.Label><b>Price</b></Form.Label>
            <Form.Control
              type="number" step="0.01" maxLength="8"
              value={price} placeholder="199.99"
              onChange={(e) => setPrice(e.target.value)}
            />
          </Form.Group>

          {/* Stock */}
          <div className="d-flex align-items-center mb-3">
            <Form.Label className="mb-0">In Stock</Form.Label>
            <Form.Check
              className="ml-2" type="checkbox"
              checked={stock} onChange={() => setStock(!stock)}
            />
          </div>

          {/* Variantes */}
          <Form.Group>
            <Form.Label><b>Sizes / Variants</b></Form.Label>

            {variants.map((row, i) => (
              <div key={i} className="d-flex align-items-center mb-2" style={{ gap: 8 }}>
                <Form.Control
                  style={{ maxWidth: 240 }}
                  placeholder="Label (e.g. 500 ml)"
                  value={row.label}
                  onChange={(e) => changeVariant(i, 'label', e.target.value)}
                />
                <Form.Control
                  type="number" style={{ maxWidth: 120 }}
                  placeholder="Size ml (opt.)"
                  value={row.size_ml}
                  onChange={(e) => changeVariant(i, 'size_ml', e.target.value)}
                />
                <Form.Control
                  type="number" step="0.01" style={{ maxWidth: 140 }}
                  placeholder="Price"
                  value={row.price}
                  onChange={(e) => changeVariant(i, 'price', e.target.value)}
                />
                <Form.Check
                  className="ml-2" label="In stock"
                  checked={row.in_stock}
                  onChange={(e) => changeVariant(i, 'in_stock', e.target.checked)}
                />
                <Form.Control
                  style={{ maxWidth: 140 }}
                  placeholder="SKU (opt.)"
                  value={row.sku}
                  onChange={(e) => changeVariant(i, 'sku', e.target.value)}
                />
                <Button variant="outline-danger" size="sm" className="ml-2"
                  onClick={() => removeVariantRow(i)}>
                  Remove
                </Button>
              </div>
            ))}

            <Button variant="outline-secondary" size="sm" className="mt-2" onClick={addVariantRow}>
              + Add size
            </Button>
          </Form.Group>

          <Button type="submit" variant="success" className="btn-sm button-focus-css mb-4">
            Save Changes
          </Button>
          <Button
            onClick={() => history.push(`/product/${productId}/`)}
            variant="primary"
            className="btn-sm ml-2 button-focus-css mb-4"
          >
            Cancel
          </Button>
        </Form>
      )}
    </div>
  );
}
