import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { Form, Button } from "react-bootstrap";
import Message from "../components/Message";
import { login, googleLogin } from "../actions/userActions";
import { GoogleLogin } from "@react-oauth/google";          // << use GSI button
import "./login-register.css";

export default function LoginPage({ history }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const dispatch = useDispatch();
  const { error, userInfo } = useSelector((s) => s.userLoginReducer || {});

  useEffect(() => {
    if (userInfo) history.push("/");
  }, [userInfo, history]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(login(username, password));
  };

  return (
    <div className="auth-page">
      <div className="auth-card">
        {/* LEFT side â€¦ keep your branding as-is */}

        <div className="auth-form">
          <h1 className="auth-title">Connexion</h1>
          <p className="auth-sub">Ravi de vous revoir ðŸ‘‹</p>

          {error && <Message variant="danger">{error}</Message>}

          <Form onSubmit={submitHandler} className="auth-form-inner">
            <Form.Group controlId="username" className="mb-3">
              <Form.Label>Nom dâ€™utilisateur</Form.Label>
              <Form.Control
                type="text"
                placeholder="Entrez votre nom dâ€™utilisateur"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="password" className="mb-4">
              <Form.Label>Mot de passe</Form.Label>
              <Form.Control
                type="password"
                placeholder="Entrez votre mot de passe"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>

            <Button type="submit" variant="dark" className="auth-btn-primary">
              Se connecter
            </Button>

            <div className="auth-divider"><span>ou</span></div>

            {/* Google button -> returns credential (id_token) */}
            <div className="auth-btn-social google">
              <GoogleLogin
                onSuccess={(cred) => {
                  const idToken = cred?.credential;
                  if (idToken) dispatch(googleLogin(idToken));
                }}
                onError={() => console.warn("Google Sign-In failed")}
                useOneTap={false}
              />
            </div>
          </Form>

          <div className="auth-switch">
            Pas de compte ? <Link to="/register">CrÃ©er un compte</Link>
          </div>
        </div>
      </div>
    </div>
  );
}
