import React from "react";
import { Link } from "react-router-dom";
import { useCart } from "../cart/CartProvider";      // ðŸ‘ˆ use the cart
import "./HomeProducts.css";                          // has .hp-card, .hp-actions, etc.

export default function HomeProductCard({ product }) {
  const cart = useCart();
  const id = product.id ?? product._id;

  const add = () => cart.addItem({
    id,
    name: product.name,
    price: product.price,
    image: product.image,
  }, 1);

  return (
    <article className="hp-card">
      {/* out of stock badge */}
      {!product.stock && <span className="hp-badge hp-badge--ko">Out of stock</span>}

      <Link to={`/product/${id}/`} className="hp-media" aria-label={product.name}>
        {/* eslint-disable-next-line jsx-a11y/alt-text */}
        <img src={product.image} />
      </Link>

      {/* ðŸ”¥ HOVER ACTIONS */}
      <div className="hp-actions">
        {/* add to cart */}
        <button className="hp-action" onClick={add} title="Ajouter au panier">
          <i className="fas fa-shopping-bag" />
        </button>

        {/* quick view / goto product */}
        <Link className="hp-action" to={`/product/${id}/`} title="Voir le produit">
          <i className="fas fa-eye" />
        </Link>
      </div>

      <div className="hp-body">
        <Link to={`/product/${id}/`} className="hp-title">
          {product.name}
        </Link>
        <div className="hp-price">
          {Number(product.price).toFixed(2)} MAD
        </div>
      </div>
    </article>
  );
}
