// src/pages/ProductDetailsPage.js
import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Spinner, Row, Col, Container, Card, Button, Modal } from 'react-bootstrap';

import { deleteProduct, getProductDetails } from '../actions/productActions';
import { toggleWishlist } from '../actions/wishlistActions';
import Message from '../components/Message';

import {
  CREATE_PRODUCT_RESET,
  DELETE_PRODUCT_RESET,
  UPDATE_PRODUCT_RESET,
  CARD_CREATE_RESET,
} from '../constants';

function ProductDetailsPage({ history, match }) {
  const dispatch = useDispatch();

  // Modal
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  // Product
  const { loading, error, product } = useSelector((state) => state.productDetailsReducer);

  // Auth
  const { userInfo } = useSelector((state) => state.userLoginReducer);

  // Deletion
  const { success: productDeletionSuccess } = useSelector((state) => state.deleteProductReducer);

  // Wishlist
  const { items: wishlistItems = [] } = useSelector((s) => s.wishlist || {});
  const pid = product?.id;
  const inWishlist = pid ? wishlistItems.some((w) => (w.id || w._id) === pid) : false;

  const handleToggleWishlist = () => {
    if (!pid) return;
    dispatch(toggleWishlist(pid));
  };

  useEffect(() => {
    dispatch(getProductDetails(match.params.id));
    dispatch({ type: UPDATE_PRODUCT_RESET });
    dispatch({ type: CREATE_PRODUCT_RESET });
    dispatch({ type: CARD_CREATE_RESET });
  }, [dispatch, match]);

  const confirmDelete = () => {
    dispatch(deleteProduct(match.params.id));
    handleClose();
  };

  if (productDeletionSuccess) {
    alert('Product successfully deleted.');
    history.push('/');
    dispatch({ type: DELETE_PRODUCT_RESET });
  }

  return (
    <div>
      {/* Delete modal */}
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>
            <i style={{ color: '#e6e600' }} className="fas fa-exclamation-triangle" /> Delete Confirmation
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete this product <em>"{product?.name}"</em>?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="danger" onClick={confirmDelete}>Confirm Delete</Button>
          <Button variant="primary" onClick={handleClose}>Cancel</Button>
        </Modal.Footer>
      </Modal>

      {loading && (
        <span style={{ display: 'flex' }}>
          <h5>Getting Product Details</h5>
          <span className="ml-2"><Spinner animation="border" /></span>
        </span>
      )}

      {error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Container>
          <Row>
            <Col md={6}>
              <Card.Img variant="top" src={product?.image} height="420" />

              {userInfo && userInfo.admin && (
                <span style={{ display: 'flex' }}>
                  <button
                    className="btn mt-2 btn-danger btn-sm button-focus-css"
                    style={{ width: '100%' }}
                    onClick={handleShow}
                  >
                    Delete Product
                  </button>

                  <button
                    className="ml-2 mt-2 btn btn-primary btn-sm button-focus-css"
                    onClick={() => history.push(`/product-update/${product.id}/`)}
                    style={{ width: '100%' }}
                  >
                    Edit Product
                  </button>
                </span>
              )}
            </Col>

            <Col sm>
              <b>{product?.name}</b>
              <hr />
              <div className="justify-description-css">
                <p>{product?.description}</p>
              </div>

              <div
                style={{
                  display: 'flex',
                  justifyContent: 'center',
                  border: '1px solid',
                  borderColor: '#C6ACE7',
                  padding: '2px',
                }}
              >
                Price:
                <span className="text-success ml-2">
                  {new Intl.NumberFormat('fr-MA', { style: 'currency', currency: 'MAD' })
                    .format(Number(product?.price || 0))}
                </span>
              </div>
            </Col>

            <Col sm>
              <b>Buy</b>
              <hr />
              {product?.stock ? (
                <Link to={`${product.id}/checkout/`}>
                  <button className="btn btn-primary">
                    <span>Pay with Stripe</span>
                  </button>
                </Link>
              ) : (
                <Message variant="danger">Out Of Stock!</Message>
              )}

              {/* Wishlist */}
              <button
                onClick={handleToggleWishlist}
                className="btn btn-outline-secondary mt-2"
                style={{ borderRadius: 10 }}
                aria-label={inWishlist ? 'Retirer de la wishlist' : 'Ajouter √† la wishlist'}
              >
                {inWishlist ? 'üíî Retirer de ma wishlist' : 'ü§ç Ajouter √† ma wishlist'}
              </button>
            </Col>
          </Row>
        </Container>
      )}
    </div>
  );
}

export default ProductDetailsPage;
