import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Form, Button, Alert } from "react-bootstrap";
import { requestPasswordReset } from "../actions/userActions";

export default function ForgotPasswordPage() {
  const [email, setEmail] = useState("");
  const dispatch = useDispatch();
  const { loading, success, error } = useSelector(s => s.passwordResetRequest || {});

  const onSubmit = (e) => {
    e.preventDefault();
    dispatch(requestPasswordReset(email));
  };

  return (
    <div className="auth-page">
      <div className="auth-card only-form">
        <h1 className="auth-title">Mot de passe oublié</h1>
        <p className="auth-sub">Entrez votre email. Nous vous enverrons un lien de réinitialisation.</p>

        {success && <Alert variant="success">Si cet email existe, un lien a été envoyé.</Alert>}
        {error && <Alert variant="danger">{String(error)}</Alert>}

        <Form onSubmit={onSubmit}>
          <Form.Group controlId="email" className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="votre@email.com"
              required
            />
          </Form.Group>
          <Button type="submit" disabled={loading}>
            {loading ? "Envoi..." : "Envoyer le lien"}
          </Button>
        </Form>
      </div>
    </div>
  );
}
