import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Form, Button, Row, Col } from 'react-bootstrap';
import { login } from '../actions/userActions';
import { googleLogin } from '../actions/userActions';
import Message from '../components/Message';
import { GoogleLogin } from '@react-oauth/google';

function LoginPage({ history }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const dispatch = useDispatch();
  const { error, userInfo } = useSelector((state) => state.userLoginReducer || {});

  useEffect(() => {
    if (userInfo) history.push('/');
  }, [userInfo, history]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(login(username, password));
  };

  return (
    <div>
      <Row className="justify-content-md-center">
        <Col xs={12} md={6}>
          <h1>Sign In</h1>
          {error && <Message variant="danger">{error}</Message>}

          <Form onSubmit={submitHandler}>
            <Form.Group controlId="username">
              <Form.Label>Username</Form.Label>
              <Form.Control
                type="text"
                placeholder="enter username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>

            <Button type="submit" variant="primary" className="w-100">Sign In</Button>
          </Form>

          {/* Google only (Facebook removed) */}
          <div className="mt-3 d-grid gap-2">
            <GoogleLogin
              onSuccess={(cred) => {
                if (cred?.credential) {
                  dispatch(googleLogin(cred.credential));
                }
              }}
              onError={() => console.warn("Google Sign-In failed")}
              theme="outline"
              shape="pill"
              size="large"
              useOneTap
            />
          </div>

          <Row className="py-3">
            <Col>
              Donâ€™t have an account? <Link to="/register">Register</Link>
            </Col>
          </Row>
        </Col>
      </Row>
    </div>
  );
}

export default LoginPage;
