import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { toggleWishlist } from "../actions/wishlistActions";
import "./HomeProducts.css";

const HomeProductCard = ({ product }) => {
  const dispatch = useDispatch();
  const { items: wishlistItems = [] } = useSelector((s) => s.wishlist || {});
  const inWishlist = wishlistItems.some((w) => (w.id || w._id) === product.id);

  const handleWishlist = () => {
    dispatch(toggleWishlist(product.id));
  };

  return (
    <div className="hp-card">
      {/* Badge */}
      {!product.stock && (
        <span className="hp-badge hp-badge--ko">Out of stock</span>
      )}

      {/* Product media */}
      <Link to={`/product/${product.id}`} className="hp-media">
        <img src={product.image} alt={product.name} />
      </Link>

      {/* Hover actions */}
      <div className="hp-actions">
        {/* View product */}
        <Link to={`/product/${product.id}`} className="hp-action" title="View">
          <i className="fas fa-eye"></i>
        </Link>

        {/* Wishlist toggle */}
        <button
          onClick={handleWishlist}
          className={`hp-action ${inWishlist ? "active" : ""}`}
          title={inWishlist ? "Remove from wishlist" : "Add to wishlist"}
        >
          <i className={inWishlist ? "fas fa-heart" : "far fa-heart"}></i>
        </button>
      </div>

      {/* Body */}
      <div className="hp-body">
        <Link to={`/product/${product.id}`} className="hp-title">
          {product.name}
        </Link>
        <div className="hp-price">
          {new Intl.NumberFormat("fr-MA", {
            style: "currency",
            currency: "MAD",
          }).format(Number(product.price || 0))}
        </div>
      </div>
    </div>
  );
};

export default HomeProductCard;
