import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";
import "./brand-marquee.css";

/**
 * Map known brand names → logo paths (put your images in /public/brands/)
 * If a brand is not in this map, we show a text fallback.
 */
const LOGOS = {
  "beauty of joseon": "/brands/boj.svg",
  "axis-y": "/brands/axisy.svg",
  "cosrx": "/brands/cosrx.svg",
  "skin1004": "/brands/skin1004.svg",
  "dr. althea": "/brands/dralthea.svg",
  "i'm from": "/brands/imfrom.svg",
  "anua": "/brands/anua.svg",
};

export default function BrandMarquee() {
  const [brands, setBrands] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let ok = true;
    (async () => {
      try {
        const { data } = await axios.get("/api/brands/");
        if (ok) setBrands(Array.isArray(data) ? data : []);
      } finally {
        if (ok) setLoading(false);
      }
    })();
    return () => { ok = false; };
  }, []);

  // duplicate list for a seamless loop
  const display = useMemo(() => [...brands, ...brands], [brands]);

  if (loading) return null;

  return (
    <section className="bm-wrap">
      <h2 className="bm-title">Toutes nos marques</h2>

      <div className="bm-rail">
        <div className="bm-track" aria-hidden="true">
          {display.map((name, i) => {
            const key = String(name || "").trim().toLowerCase();
            const src = LOGOS[key];
            return (
              <div className="bm-card" key={`${name}-${i}`}>
                {src ? (
                  <img src={src} alt={name} />
                ) : (
                  <span className="bm-text">{name}</span>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
}
