import React from "react";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { toggleWishlist } from "../actions/wishlistActions";
import { useCart } from "../cart/CartProvider";
import "./HomeProducts.css";

export default function HomeProductCard({ product }) {
  const id = product.id ?? product._id;
  const dispatch = useDispatch();
  const cart = useCart();

  const addToCart = () =>
    cart.addItem(
      {
        id,
        name: product.name,
        price: Number(product.price),
        image: product.image,
      },
      1
    );

  const addToWishlist = (e) => {
    e.preventDefault();
    dispatch(toggleWishlist(id));
  };

  const hasDiscount =
    (product.has_discount === true) ||
    (product.old_price && Number(product.old_price) > Number(product.price));

  const discountPercent = hasDiscount
    ? (product.discount_percent ??
       Math.round(((Number(product.old_price) - Number(product.price)) / Number(product.old_price)) * 100))
    : null;

  return (
    <article className="hp-card">
      {/* out of stock badge */}
      {!product.stock && (
        <span className="hp-badge hp-badge--ko">Out of stock</span>
      )}

      {/* ✅ promo badge */}
      {hasDiscount && (
        <span className="hp-badge hp-badge--promo">
          -{discountPercent}%
        </span>
      )}

      <Link to={`/product/${id}/`} className="hp-media" aria-label={product.name}>
        {/* eslint-disable-next-line jsx-a11y/alt-text */}
        <img src={product.image} />
      </Link>

      {/* hover icons */}
      <div className="hp-actions-row">
        <button
          type="button"
          className="hp-action-square"
          title="Ajouter à la wishlist"
          onClick={addToWishlist}
        >
          <i className="fas fa-heart" />
        </button>

        <Link
          className="hp-action-square"
          to={`/product/${id}/`}
          title="Voir le produit"
        >
          <i className="fas fa-eye" />
        </Link>
      </div>

      {/* big add bar */}
      <button type="button" className="hp-addbar" onClick={addToCart}>
        <i className="fas fa-shopping-bag mr-2" />
        AJOUTER AU PANIER
      </button>

      {/* body */}
      <div className="hp-body">
        <Link to={`/product/${id}/`} className="hp-title">
          {product.name}
        </Link>

        {/* ✅ price zone: if discount show both, else show single bold black */}
        {hasDiscount ? (
          <div className="hp-pricebox">
            <span className="hp-price-old">
              {Number(product.old_price).toFixed(2)} MAD
            </span>
            <span className="hp-price-new">
              {Number(product.price).toFixed(2)} MAD
            </span>
          </div>
        ) : (
          <div className="hp-price">{Number(product.price).toFixed(2)} MAD</div>
        )}
      </div>
    </article>
  );
}
