// src/components/HomeProductCard.js
import React from "react";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { toggleWishlist } from "../actions/wishlistActions";
import { useCart } from "../cart/CartProvider";
import "./HomeProducts.css";

export default function HomeProductCard({ product }) {
  const id = product.id ?? product._id;
  const dispatch = useDispatch();
  const cart = useCart();

  const addToCart = () =>
    cart.addItem(
      {
        id,
        name: product.name,
        price: Number(product.price),
        image: product.image,
      },
      1
    );

  const addToWishlist = (e) => {
    e.preventDefault();
    dispatch(toggleWishlist(id));
  };

  const hasDiscount = !!product?.has_discount;
  const percent = Number(product?.discount_percent || 0);

  return (
    <article className="hp-card">
      {/* top-left badge: out of stock */}
      {!product.stock && (
        <span className="hp-badge hp-badge--ko">Out of stock</span>
      )}

      {/* top-right badge: -% sale */}
      {hasDiscount && (
        <span className="hp-badge hp-badge--sale">-{percent}%</span>
      )}

      <Link to={`/product/${id}/`} className="hp-media" aria-label={product.name}>
        {/* eslint-disable-next-line jsx-a11y/alt-text */}
        <img src={product.image} />
      </Link>

      {/* hover action squares */}
      <div className="hp-actions-row">
        <button
          type="button"
          className="hp-action-square"
          title="Ajouter Ã  la wishlist"
          onClick={addToWishlist}
        >
          <i className="fas fa-heart" />
        </button>

        <Link
          className="hp-action-square"
          to={`/product/${id}/`}
          title="Voir le produit"
        >
          <i className="fas fa-eye" />
        </Link>
      </div>

      {/* big add-to-cart bar on hover */}
      <button type="button" className="hp-addbar" onClick={addToCart}>
        <i className="fas fa-shopping-bag mr-2" />
        AJOUTER AU PANIER
      </button>

      {/* body */}
      <div className="hp-body">
        <Link to={`/product/${id}/`} className="hp-title">
          {product.name}
        </Link>

        {/* price block with old/new */}
        <div className="hp-price-wrap">
          {hasDiscount && (
            <span className="hp-price-old">
              {Number(product.old_price).toFixed(2)} MAD
            </span>
          )}
          <span className="hp-price-new">
            {Number(product.price).toFixed(2)} MAD
          </span>
        </div>
      </div>
    </article>
  );
}
