// src/pages/ProductsListPage.js
import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";
import { Container, Spinner, Alert } from "react-bootstrap";

// ✅ reuse the same glossy card + grid styles as Home
import "../components/HomeProducts.css";
import HomeProductCard from "../components/HomeProductCard";

const ProductsListPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading]   = useState(true);
  const [error, setError]       = useState(null);

  // simple client-side search (optional)
  const [q, setQ] = useState("");

  useEffect(() => {
    let alive = true;
    (async () => {
      try {
        const { data } = await axios.get("/api/products/");
        if (!alive) return;
        setProducts(Array.isArray(data) ? data : []);
      } catch (e) {
        if (alive) setError(e?.response?.data?.detail || e.message);
      } finally {
        if (alive) setLoading(false);
      }
    })();
    return () => { alive = false; };
  }, []);

  const filtered = useMemo(() => {
    const needle = q.trim().toLowerCase();
    if (!needle) return products;
    return products.filter(p =>
      (p.name || "").toLowerCase().includes(needle) ||
      (p.description || "").toLowerCase().includes(needle)
    );
  }, [products, q]);

  return (
    <Container className="py-5">
      {/* Page title + search */}
      <div className="d-flex flex-wrap align-items-center justify-content-between mb-3">
        <h2 className="m-0" style={{ fontWeight: 900 }}>Tous les produits</h2>

        <div className="d-flex align-items-center gap-2">
          <input
            type="search"
            className="form-control"
            placeholder="Rechercher un produit…"
            style={{ minWidth: 260 }}
            value={q}
            onChange={(e) => setQ(e.target.value)}
          />
        </div>
      </div>

      {/* Loading / Error */}
      {loading && (
        <div className="d-flex justify-content-center py-5">
          <Spinner animation="border" />
        </div>
      )}
      {error && <Alert variant="danger">{error}</Alert>}

      {/* Grid */}
      {!loading && !error && (
        <>
          {filtered.length > 0 ? (
            <div className="hp-grid">
              {filtered.map((p) => (
                <HomeProductCard key={p.id} product={p} />
              ))}
            </div>
          ) : (
            <div className="text-center text-muted py-5">
              Aucun produit trouvé.
            </div>
          )}
        </>
      )}
    </Container>
  );
};

export default ProductsListPage;
