// A single axios instance with an auth interceptor
import axios from "axios";

const api = axios.create({
  // baseURL: "/",
  // withCredentials: false,
});

api.interceptors.request.use((config) => {
  try {
    const raw = localStorage.getItem("userInfo");
    if (raw) {
      const u = JSON.parse(raw);
      // support different backends: Token, JWT, custom key
      const token = u?.token || u?.access || u?.key || u?.authToken;
      if (token) {
        // DRF Token:  `Token <key>`
        // JWT:        `Bearer <access>`
        const scheme = u?.access ? "Bearer" : (u?.token ? "Token" : "Bearer");
        config.headers.Authorization = `${scheme} ${token}`;
      }
    }
  } catch (_) {}
  return config;
});

export default api;
