// src/pages/HomePage.js
import React, { useEffect, useMemo, useState } from 'react';
import { Container, Row, Col, Button, Spinner, Alert, Nav } from 'react-bootstrap';
import axios from 'axios';
import "../components/HomeProducts.css";
import HomeProductCard from "../components/HomeProductCard";
import WhatsAppFloat from "../components/WhatsAppFloat";
import BrandMarquee from "../components/BrandMarquee";
import SiteFooter from "../components/SiteFooter";

const CATS = [
  { key: "face",    label: "VISAGE"  },
  { key: "lips",    label: "L√àVRES"  },
  { key: "eyes",    label: "YEUX"    },
  { key: "eyebrow", label: "SOURCILS"},
  { key: "hair",    label: "CHEVEUX" },
];

export default function HomePage() {
  const [tab, setTab]           = useState("face");
  const [loading, setLoading]   = useState(true);
  const [error, setError]       = useState(null);
  const [products, setProducts] = useState([]);

  const [featured, setFeatured] = useState([]);
  const [loadingFeatured, setLoadingFeatured] = useState(true);
  const [errorFeatured, setErrorFeatured]     = useState(null);

  const banners = useMemo(() => ([
    '/hero/banner1.jpg',
    '/hero/banner2.jpg',
    '/hero/banner3.jpg',
    '/hero/banner4.jpg',
    '/hero/banner5.jpg',
  ]), []);

  // featured
  useEffect(() => {
    let ok = true;
    (async () => {
      try {
        const { data } = await axios.get('/api/products/');
        if (ok) setFeatured((data || []).slice(0, 8));
      } catch (e) {
        if (ok) setErrorFeatured(e?.response?.data?.detail || e.message);
      } finally {
        if (ok) setLoadingFeatured(false);
      }
    })();
    return () => { ok = false; };
  }, []);

  // current tab products
  useEffect(() => {
    let alive = true;
    setLoading(true);
    setError(null);
    setProducts([]);

    (async () => {
      try {
        const { data } = await axios.get(`/api/products/?type=${encodeURIComponent(tab)}`);
        if (!alive) return;
        setProducts(Array.isArray(data) ? data : []);
      } catch (e) {
        if (alive) setError(e?.response?.data?.detail || e.message);
      } finally {
        if (alive) setLoading(false);
      }
    })();

    return () => { alive = false; };
  }, [tab]);

  return (
    <>
      {/* ===== HERO strip (auto-scroll to the right) ===== */}
      <section className="hero-strip">
        <div className="hero-overlay text-center text-white">
          <h1 className="display-4 mb-3">Welcome to Your Beauty Shop</h1>
          <p className="lead mb-4">
            Safe, effective routines for hydration, brightening and barrier repair ‚Äî shipped fast.
          </p>
          <Button size="lg" variant="light" href="/products">Discover our products</Button>
        </div>

        <div className="hero-track">
          {banners.map((src, i) => (
            <img key={`a-${i}`} className="hero-tile" src={src} alt={`banner ${i + 1}`} />
          ))}
          {banners.map((src, i) => (
            <img key={`b-${i}`} className="hero-tile" src={src} alt={`banner dup ${i + 1}`} />
          ))}
        </div>
      </section>

      {/* ===== Featured ===== */}
      <Container className="py-5">
        <Row className="align-items-center mb-3">
          <Col><h2 className="m-0 fw-900">Featured this week</h2></Col>
          <Col className="text-right">
            <Button variant="outline-secondary" size="sm" href="/products">See all</Button>
          </Col>
        </Row>

        {loadingFeatured && (
          <div className="d-flex justify-content-center py-5">
            <Spinner animation="border" />
          </div>
        )}
        {errorFeatured && <Alert variant="danger">{errorFeatured}</Alert>}

        {!loadingFeatured && !errorFeatured && (
          featured.length ? (
            <div className="hp-grid">
              {featured.map((p) => <HomeProductCard key={p.id} product={p} />)}
            </div>
          ) : (
            <div className="text-center text-muted py-5">No featured products yet.</div>
          )
        )}
      </Container>

      {/* ===== Category shelf (tabs) ===== */}
      <Container className="py-4">
        <div className="hp-tabs">
          <Nav
            variant="tabs"
            activeKey={tab}
            onSelect={(k) => setTab(k || "face")}
            className="justify-content-center"
          >
            {CATS.map(({ key, label }) => (
              <Nav.Item key={key}>
                <Nav.Link eventKey={key} className={`hp-tab ${tab === key ? "is-active" : ""}`}>
                  {label}
                </Nav.Link>
              </Nav.Item>
            ))}
          </Nav>
        </div>

        <div className="mt-4">
          {loading && (
            <div className="d-flex justify-content-center py-5">
              <Spinner animation="border" />
            </div>
          )}
          {error && <Alert variant="danger">{error}</Alert>}

          {!loading && !error && (
            products.length ? (
              <div className="hp-grid">
                {products.map((p) => <HomeProductCard key={p.id} product={p} />)}
              </div>
            ) : (
              <div className="text-center text-muted py-5">Aucun produit dans cette cat√©gorie.</div>
            )
          )}
        </div>
      </Container>

      {/* ===== Moving brands marquee ===== */}
      <BrandMarquee />

      {/* ===== Skincare video spotlight ===== */}
      <section className="video-section">
        <Container>
          <Row className="align-items-center">
            <Col md={5} className="video-text mb-4 mb-md-0">
              <h2 className="video-title">D√©couvrez la beaut√© au naturel üåø</h2>
              <p className="video-desc">
                Prenez un moment pour vous. D√©couvrez notre gamme de soins inspir√©e par la nature
                et con√ßue pour sublimer votre peau. Des gestes simples, des r√©sultats visibles ‚Äî
                votre rituel beaut√© commence ici.
              </p>
              <Button variant="dark" size="lg" href="/products">Explorer les soins</Button>
            </Col>
            <Col md={7} className="video-col">
              <div className="video-wrapper">
                <video
                  autoPlay loop muted playsInline
                  className="skincare-video"
                  poster="/video/preview.jpg"
                >
                  <source src="/video/skincare.mp4" type="video/mp4" />
                  Votre navigateur ne supporte pas la lecture vid√©o.
                </video>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
      

      {/* ===== WhatsApp floating button ===== */}
      <WhatsAppFloat
        phone="212656443866"
        label="WhatsApp"
        message="Salut ! J‚Äôaimerais des conseils sur vos produits üòä"
        position="left"
      />
    </>
    
  );
}
