// src/pages/WishlistPage.js
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchWishlist, toggleWishlist } from '../actions/wishlistActions';
import { Link } from 'react-router-dom';
import Message from '../components/Message';

export default function WishlistPage() {
  const dispatch = useDispatch();
  const { loading, error, items } = useSelector((s) => s.wishlist);

  useEffect(() => {
    dispatch(fetchWishlist());
  }, [dispatch]);

  if (loading) return <div className="p-3">Loadingâ€¦</div>;
  if (error)   return <Message variant="danger">{error}</Message>;

  if (!items || items.length === 0) {
    return (
      <div className="p-3">
        <h4>Your wishlist is empty.</h4>
        <p><Link to="/products">Browse products</Link></p>
      </div>
    );
  }

  return (
    <div className="p-3">
      <h3 className="mb-3">My Wishlist</h3>
      <div className="row">
        {items.map(p => (
          <div key={p.id} className="col-12 col-sm-6 col-md-4 col-lg-3 mb-4">
            <div className="card h-100">
              <img src={p.image} className="card-img-top" alt={p.name} />
              <div className="card-body">
                <h6 className="card-title">{p.name}</h6>
                <p className="text-success mb-2">
                  {new Intl.NumberFormat('fr-MA', { style:'currency', currency:'MAD' }).format(Number(p.price))}
                </p>
                <div className="d-flex gap-2">
                  <Link to={`/product/${p.id}/`} className="btn btn-sm btn-outline-primary">View</Link>
                  <button
                    className="btn btn-sm btn-outline-danger"
                    onClick={() => dispatch(toggleWishlist(p.id))}
                  >
                    Remove
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
