// src/pages/LoginPage.jsx
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { Form, Button } from "react-bootstrap";
import { login } from "../actions/userActions";
import "./login-register.css";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory();
  const dispatch = useDispatch();
  const { error, userInfo } = useSelector((s) => s.userLoginReducer || {});

  useEffect(() => {
    if (userInfo) history.push("/");
  }, [userInfo, history]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(login(email, password));
  };

  return (
    <div className="auth-page">
      <div className="auth-card">
        <div className="auth-side">
          <img src="/brand/logop.png" alt="MiniGlow" className="auth-logo" />
          <h2 className="auth-headline">Connexion</h2>
          <p className="auth-blurb">Ravi de vous revoir ðŸ‘‹</p>
        </div>

        <div className="auth-form">
          <h1 className="auth-title">Connexion</h1>
          {error && <div className="alert danger">{String(error)}</div>}

          <Form onSubmit={submitHandler} className="auth-form-inner">
            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="vous@exemple.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-4">
              <Form.Label>Mot de passe</Form.Label>
              <Form.Control
                type="password"
                placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </Form.Group>

            <Button type="submit" variant="dark" className="auth-btn-primary">
              Se connecter
            </Button>

            <div className="auth-meta">
              <Link to="/forgot-password">Mot de passe oubliÃ© ?</Link>
            </div>
          </Form>

          <div className="auth-switch">
            Pas de compte ? <Link to="/register">CrÃ©er un compte</Link>
          </div>
        </div>
      </div>
    </div>
  );
}
