// src/pages/CheckoutPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Alert, Button } from "react-bootstrap";

export default function CheckoutPage() {
  const [cart, setCart] = useState([]);
  const [email, setEmail] = useState("");
  const [ship, setShip] = useState(0);
  const [error, setError] = useState("");
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    try {
      const raw = localStorage.getItem("cart") || "[]";
      const items = JSON.parse(raw);
      setCart(Array.isArray(items) ? items : []);
    } catch {
      setCart([]);
    }
  }, []);

  const total =
    cart.reduce((s, i) => s + Number(i.price || 0) * Number(i.qty || 1), 0) +
    Number(ship || 0);

  const checkout = async () => {
    setError("");
    if (!cart.length) {
      setError("Votre panier est vide.");
      return;
    }
    setSubmitting(true);
    try {
      const payload = {
        items: cart.map((i) => ({
          price: Number(i.price),
          qty: Number(i.qty || 1),
        })),
        shipping: { price: Number(ship || 0) },
        email: email || "",
      };
      const { data } = await axios.post("/api/payments/create/", payload);
      localStorage.setItem("lastOrderId", String(data.order_id));
      window.location.href = data.payment_url; // redirection (simulée banque)
    } catch (e) {
      setError(
        e?.response?.data?.detail || e.message || "Erreur lors du paiement"
      );
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="container py-4">
      <h2>Paiement</h2>
      {error && <Alert variant="danger" className="mt-3">{error}</Alert>}

      <div className="mt-3">
        <h5>Panier</h5>
        {!cart.length ? (
          <div className="text-muted">Votre panier est vide.</div>
        ) : (
          <ul>
            {cart.map((i, idx) => (
              <li key={idx}>
                {i.name} — {Number(i.price).toFixed(2)} × {i.qty || 1}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="mt-3">
        <label>Email (optionnel)</label>
        <input
          className="form-control"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>

      <div className="mt-3">
        <label>Frais de livraison (MAD)</label>
        <input
          className="form-control"
          type="number"
          value={ship}
          onChange={(e) => setShip(e.target.value)}
        />
      </div>

      <div className="mt-3">
        <b>Total :</b> {total.toFixed(2)} MAD
      </div>

      <Button className="mt-4" onClick={checkout} disabled={submitting}>
        {submitting ? "Veuillez patienter..." : "Passer à la caisse"}
      </Button>
    </div>
  );
}
