import React from "react";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { toggleWishlist } from "../actions/wishlistActions";
import { useCart } from "../cart/CartProvider";
import "./HomeProducts.css";

export default function HomeProductCard({ product }) {
  const id = product.id ?? product._id;
  const dispatch = useDispatch();
  const cart = useCart();

  const base = Number(product?.price ?? 0);
  const promo = product?.new_price != null ? Number(product.new_price) : NaN;

  const hasDiscount =
    (!!product?.has_discount) ||
    (Number.isFinite(promo) && promo > 0 && promo < base);

  const percent = Number.isFinite(Number(product?.discount_percent))
    ? Number(product.discount_percent)
    : hasDiscount ? Math.round((1 - promo / base) * 100) : 0;

  const effective = hasDiscount ? promo : base;

  const addToCart = () =>
    cart.addItem({ id, name: product.name, price: effective, image: product.image }, 1);

  const addToWishlist = (e) => { e.preventDefault(); dispatch(toggleWishlist(id)); };

  return (
    <article className="hp-card">
      {!product.stock && <span className="hp-badge hp-badge--ko">Out of stock</span>}
      {hasDiscount && <span className="hp-badge hp-badge--sale">-{percent}%</span>}

      <Link to={`/product/${id}/`} className="hp-media" aria-label={product.name}>
        <img src={product.image} alt={product.name} />
      </Link>

      <div className="hp-actions-row">
        <button type="button" className="hp-action-square" title="Ajouter Ã  la wishlist" onClick={addToWishlist}>
          <i className="fas fa-heart" />
        </button>
        <Link className="hp-action-square" to={`/product/${id}/`} title="Voir le produit">
          <i className="fas fa-eye" />
        </Link>
      </div>

      <button type="button" className="hp-addbar" onClick={addToCart}>
        <i className="fas fa-shopping-bag mr-2" /> AJOUTER AU PANIER
      </button>

      <div className="hp-body">
        <Link to={`/product/${id}/`} className="hp-title">{product.name}</Link>
        <div className="hp-price-wrap">
          {hasDiscount && <span className="hp-price-old">{base.toFixed(2)} MAD</span>}
          <span className="hp-price-new">{effective.toFixed(2)} MAD</span>
        </div>
      </div>
    </article>
  );
}
