import { useHistory } from 'react-router-dom';
import { getProductDetails, updateProduct } from '../actions/productActions';
import { checkTokenValidation, logout } from '../actions/userActions';
import { UPDATE_PRODUCT_RESET } from '../constants';
import Message from '../components/Message';

export default function ProductUpdatePage({ match }) {
  const productId = match.params.id;
  const history = useHistory();
  const dispatch = useDispatch();

  // Base fields
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('other');
  const [price, setPrice] = useState('');        // regular price
  const [newPrice, setNewPrice] = useState('');  // promo price (optional)
  const [discountPct, setDiscountPct] = useState(''); // helper control
  const [stock, setStock] = useState(false);

  // Image
  const [newImage, setNewImage] = useState(false);
  const [image, setImage] = useState(null);

  // Variants (unchanged)
  const [variants, setVariants] = useState([]);
  const addVariantRow = () => setVariants(v => [...v, { label: '', size_ml: '', price: '', in_stock: true, sku: '' }]);
  const removeVariantRow = (idx) => setVariants(v => v.filter((_, i) => i !== idx));
  const changeVariant = (idx, field, value) => setVariants(v => v.map((row, i) => (i === idx ? { ...row, [field]: value } : row)));

  // Reducers
  const { userInfo } = useSelector(s => s.userLoginReducer || {});
  const { product, loading: loadingDetails } = useSelector(s => s.productDetailsReducer || {});
  const { success, loading: loadingUpdate, error } = useSelector(s => s.updateProductReducer || {});
  const { error: tokenError } = useSelector(s => s.checkTokenValidationReducer || {});

  useEffect(() => {
    if (!userInfo || !userInfo.admin) { history.push('/login'); return; }
    dispatch(checkTokenValidation());
    dispatch(getProductDetails(productId));
  }, [dispatch, userInfo, history, productId]);

  useEffect(() => {
    if (!product || !product.id) return;
    setName(product.name || '');
    setDescription(product.description || '');
    setBrand(product.brand || '');
    setCategory(product.category || 'other');
    setPrice(String(product.price ?? ''));
    setNewPrice(product.new_price != null ? String(product.new_price) : '');
    setStock(!!product.stock);
    setVariants(
      (product.variants || []).map(v => ({
        label: v.label || '',
        size_ml: v.size_ml ?? '',
        price: String(v.price ?? ''),
        in_stock: !!v.in_stock,
        sku: v.sku || '',
      }))
    );
  }, [product]);

  // Helper: compute newPrice from % helper
  const applyPercent = () => {
    const op = parseFloat(price);
    const pct = parseFloat(discountPct);
    if (!isFinite(op) || !isFinite(pct) || op <= 0 || pct <= 0) return;
    const np = Math.round(op * (100 - pct)) / 100; // MAD has 2 decimals, but rounded here is fine
    setNewPrice(String(np.toFixed(2)));
  };

  const onSubmit = (e) => {
    e.preventDefault();
    const fd = new FormData();

    fd.append('name', name);
    fd.append('description', description);
    fd.append('price', price);
    // send empty string to clear promo
    if (newPrice === '' || newPrice === null) {
      fd.append('new_price', '');
    } else {
      fd.append('new_price', newPrice);
    }
    fd.append('stock', stock);
    fd.append('brand', brand);
    fd.append('category', category);
    if (newImage && image) fd.append('image', image);

    const clean = variants
      .filter(v => v.label && String(v.price) !== '')
      .map(v => ({
        label: v.label,
        size_ml: v.size_ml === '' ? null : Number(v.size_ml),
        price: Number(v.price),
        in_stock: !!v.in_stock,
        sku: v.sku || ''
      }));
    if (clean.length) fd.append('variants', JSON.stringify(clean));

    dispatch(updateProduct(productId, fd));
  };

  if (success) {
    alert('Product successfully updated.');
    dispatch({ type: UPDATE_PRODUCT_RESET });
    history.push(`/product/${productId}/`);
  }

  if (userInfo && tokenError === 'Request failed with status code 401') {
    alert('Session expired, please login again.');
    dispatch(logout());
    history.push('/login');
    window.location.reload();
  }

  return (
    <div>
      <span className="d-flex justify-content-center text-info">
        <em>Edit Product</em>
      </span>

      {error && <Message variant="danger">{error.image ? error.image[0] : String(error)}</Message>}

      {loadingDetails && (
        <span style={{ display: 'flex' }}>
          <h5>Getting Product Details</h5>
          <span className="ml-2"><Spinner animation="border" /></span>
        </span>
      )}

      {loadingUpdate && (
        <span style={{ display: 'flex' }}>
          <h5>Updating Product</h5>
          <span className="ml-2"><Spinner animation="border" /></span>
        </span>
      )}

      {product && (
        <Form onSubmit={onSubmit}>
          <Form.Group controlId="image">
            <Form.Label><b>Product Image</b></Form.Label>
            <p>{product.image && <img src={product.image} alt={product.name} height="200" />}</p>

            {newImage ? (
              <>
                <Form.Control type="file" onChange={(e) => setImage(e.target.files[0])} />
                <span onClick={() => { setNewImage(false); setImage(null); }} className="btn btn-primary btn-sm mt-2">
                  Cancel
                </span>
              </>
            ) : (
              <p><span onClick={() => setNewImage(true)} className="btn btn-success btn-sm">choose different image</span></p>
            )}
          </Form.Group>

          <Form.Group controlId="name"><Form.Label><b>Product Name</b></Form.Label>
            <Form.Control autoFocus value={name} onChange={(e) => setName(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="description"><Form.Label><b>Product Description</b></Form.Label>
            <Form.Control value={description} onChange={(e) => setDescription(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="brand"><Form.Label><b>Brand (Marque)</b></Form.Label>
            <Form.Control value={brand} onChange={(e) => setBrand(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="category"><Form.Label><b>Category</b></Form.Label>
            <Form.Control as="select" value={category} onChange={(e) => setCategory(e.target.value)}>
              <option value="face">Face</option>
              <option value="lips">Lips</option>
              <option value="eyes">Eyes</option>
              <option value="eyebrow">Eyebrow</option>
              <option value="hair">Hair</option>
              <option value="other">Other</option>
            </Form.Control>
          </Form.Group>

          <Form.Group controlId="price"><Form.Label><b>Regular price</b></Form.Label>
            <Form.Control type="number" step="0.01" value={price} onChange={(e) => setPrice(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="new_price"><Form.Label><b>New price (promo, optional)</b></Form.Label>
            <Form.Control type="number" step="0.01" value={newPrice} onChange={(e) => setNewPrice(e.target.value)} />
            <small className="text-muted">Leave empty to remove the promotion.</small>
          </Form.Group>

          <Form.Group controlId="discountPct"><Form.Label><b>Discount % helper</b></Form.Label>
            <InputGroup>
              <Form.Control type="number" step="1" min="1" max="95" value={discountPct} placeholder="ex. 22"
                onChange={(e) => setDiscountPct(e.target.value)} />
              <InputGroup.Append>
                <Button variant="outline-secondary" onClick={applyPercent}>Apply to New price</Button>
              </InputGroup.Append>
            </InputGroup>
            <small className="text-muted">Based on Regular price.</small>
          </Form.Group>

          <div className="d-flex align-items-center mb-3">
            <Form.Label className="mb-0">In Stock</Form.Label>
            <Form.Check className="ml-2" type="checkbox" checked={stock} onChange={() => setStock(!stock)} />
          </div>

          {/* Variants unchanged */}
          <Form.Group>
            <Form.Label><b>Sizes / Variants</b></Form.Label>
            {variants.map((row, i) => (
              <div key={i} className="d-flex align-items-center mb-2" style={{ gap: 8 }}>
                <Form.Control style={{ maxWidth: 240 }} placeholder="Label (e.g. 500 ml)"
                  value={row.label} onChange={(e) => changeVariant(i, 'label', e.target.value)} />
                <Form.Control type="number" style={{ maxWidth: 120 }} placeholder="Size ml (opt.)"
                  value={row.size_ml} onChange={(e) => changeVariant(i, 'size_ml', e.target.value)} />
                <Form.Control type="number" step="0.01" style={{ maxWidth: 140 }} placeholder="Price"
                  value={row.price} onChange={(e) => changeVariant(i, 'price', e.target.value)} />
                <Form.Check className="ml-2" label="In stock" checked={row.in_stock}
                  onChange={(e) => changeVariant(i, 'in_stock', e.target.checked)} />
                <Form.Control style={{ maxWidth: 140 }} placeholder="SKU (opt.)"
                  value={row.sku} onChange={(e) => changeVariant(i, 'sku', e.target.value)} />
                <Button variant="outline-danger" size="sm" className="ml-2" onClick={() => removeVariantRow(i)}>Remove</Button>
              </div>
            ))}
            <Button variant="outline-secondary" size="sm" className="mt-2" onClick={addVariantRow}>+ Add size</Button>
          </Form.Group>

          <Button type="submit" variant="success" className="btn-sm mb-4">Save Changes</Button>
          <Button onClick={() => history.push(`/product/${productId}/`)} variant="primary" className="btn-sm ml-2 mb-4">Cancel</Button>
        </Form>
      )}
    </div>
  );
}
